sail_code:
if(
  a!isNullOrEmpty(ri!parameters.instanceMap),
  false,
  a!map(
    result: if(
      a!isNullOrEmpty(ri!parameters.instanceMap),
      false,
      and(
        a!isNotNullOrEmpty(ri!parameters.instanceMap.firstName),
        a!isNotNullOrEmpty(ri!parameters.instanceMap.lastName)        
      )
    ),
    message: if(
      and(
        a!isNotNullOrEmpty(ri!parameters.instanceMap.firstName),
        a!isNotNullOrEmpty(ri!parameters.instanceMap.lastName),

      ),
      {},
      "Either First Name or Last Name is not available"
    )
  )
)
