sail_code:
if(
  a!isNullOrEmpty(ri!value),
  null,
  if(
    or(
      regexmatch("^\d{10}$", ri!value),
      regexmatch("^\d{3}-\d{3}-\d{4}$", ri!value),
      regexmatch("^\(\d{3}\) \d{3}-\d{4}$", ri!value)
    ),
    null,
    "Please enter a valid 10-digit phone number in one of these formats: 1234567890, 123-456-7890, or (123) 456-7890."
  )
)
