sail_code:
#"SYSTEM_SYSRULES_cardGroupLayout"(
  showWhen: ri!showWhen,
  labelPosition: "COLLAPSED",
  cards: {
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: {
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"SYSTEM_SYSRULES_sideBySideLayout"(
                  items: {
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: #"SYSTEM_SYSRULES_richTextIcon"(
                          icon: "file",
                          size: "LARGE",
                          color: "ACCENT"
                        )
                      ),
                      width: "MINIMIZE"
                    ),
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: {
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: #"urn:appian:translation-string:v1:c6e59a5b-39ea-4713-a231-1d309996f3f3",
                            style: "STRONG"
                          ),
                          char(
                            10
                          ),
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: #"urn:appian:translation-string:v1:6f27a0d3-0b14-4873-8b29-a7e9f97b0b31",
                            color: ri!brandingMap.Gray3
                          )
                        }
                      )
                    )
                  },
                  alignVertical: "MIDDLE"
                )
              }
            ),
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
                  value: #"SYSTEM_SYSRULES_richTextIcon"(
                    icon: if(
                      ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = false,
                      "dot-circle-large",
                      "circle-o-large"
                    ),
                    color: if(
                      ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = false,
                      "ACCENT",
                      "SECONDARY"
                    )
                  ),
                  align: "RIGHT"
                )
              },
              width: "EXTRA_NARROW"
            )
          },
          alignVertical: "TOP",
          showDividers: false
        )
      },
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: {
          /*If this choice was already selected, then no need to save anything*/
          if(
            ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = false,
            {},
            {
              a!save(
                ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs],
                false
              ),
              a!save(
                ri!classificationModelVersion[recordType!AIA Classification Model Version.categories],
                /*The "Other" category will always have the highest possible sortOrder*/
                /*Don't have to hard delete the Other category since Allow Multiple Outputs can only be edited when creating a new Model Version*/
                remove(
                  ri!classificationModelVersion[recordType!AIA Classification Model Version.categories],
                  rule!AIA_UTIL_Filter(
                    records: ri!classificationModelVersion[recordType!AIA Classification Model Version.categories],
                    field: recordType!AIA Classification Model Version Category.sortOrder,
                    value: max(ri!classificationModelVersion[recordType!AIA Classification Model Version.categories.sortOrder]),
                    filterType: "MATCH_INDICES"
                  )
                )
              )
            }
          )
        }
      ),
      borderColor: if(
        ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = false,
        "ACCENT",
        null
      ),
      shape: "SEMI_ROUNDED"
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: {
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"SYSTEM_SYSRULES_sideBySideLayout"(
                  items: {
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: #"SYSTEM_SYSRULES_richTextIcon"(
                          icon: "files-solid",
                          size: "LARGE",
                          color: "ACCENT"
                        )
                      ),
                      width: "MINIMIZE"
                    ),
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                        labelPosition: "COLLAPSED",
                        value: {
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: #"urn:appian:translation-string:v1:1d06bf75-460d-4870-90e1-f16831bc9b29",
                            style: "STRONG"
                          ),
                          char(
                            10
                          ),
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: #"urn:appian:translation-string:v1:1610afdd-70df-4a55-bb30-7bb5d59b4785",
                            color: ri!brandingMap.Gray3
                          )
                        }
                      )
                    )
                  },
                  alignVertical: "MIDDLE"
                )

              }
            ),
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
                  value: #"SYSTEM_SYSRULES_richTextIcon"(
                    icon: if(
                      ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = true,
                      "dot-circle-large",
                      "circle-o-large"
                    ),
                    color: if(
                      ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = true,
                      "ACCENT",
                      "SECONDARY"
                    )
                  ),
                  align: "RIGHT"
                )
              },
              width: "EXTRA_NARROW"
            )
          },
          alignVertical: "TOP"
        )
      },
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto:
        /*If this choice was already selected, then no need to save anything*/
        if(
          ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = true,
          {},
          {
            a!save(
              ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs],
              true
            ),
            a!save(
              ri!classificationModelVersion[recordType!AIA Classification Model Version.categories],
              append(
                ri!classificationModelVersion[recordType!AIA Classification Model Version.categories],
                rule!AIA_ClassificationModelVersionCategory_Initialize(
                  name: #"urn:appian:translation-string:v1:a8b8fd7a-5388-4951-bf89-254fbbe97592",
                  sortOrder: length(ri!classificationModelVersion[recordType!AIA Classification Model Version.categories]) + 1
                )
              )
            )
          }
        )
      ),
      borderColor: if(
        ri!classificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = true,
        "ACCENT",
        null
      ),
      shape: "SEMI_ROUNDED"
    )

  },
  marginAbove: "STANDARD",
  marginBelow: "STANDARD",
  fillContainer: true,
  spacing: "DENSE"
)
