if(
  or(
    rule!AS_CO_UT_isBlank(ri!usernameList),
    rule!AS_CO_UT_isBlank(index(
      ri!entityMap,
      "entity",
      null
    )),
    rule!AS_CO_UT_isBlank(index(
      ri!entityMap,
      "entityType",
      null
    ))
  ),
  {},
  a!localVariables(
    local!usersInSystem: rule!AS_GAM_QE_getUser(
      usernames:  ri!usernameList,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      entityMap: ri!entityMap
    ),
    /*reject runs over results to get rid of all blanks. If no results, will skip DB write in process model*/
    local!usersForDb: #"SYSTEM_SYSRULES_forEach"(
      items: ri!usernameList,
      expression: if(
        /*is the username valid?*/
        not(rule!AS_CO_UT_checkIfUserExists(user: fv!item)),
        /*Not a valid user. Return empty*/
        {},
        a!localVariables(
          local!filteredUser: index(
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!usersInSystem,
              field: "username",
              value: fv!item
            ),
            1,
            {}
          ),
          local!userStatus: user(fv!item, "status"),
          local!userFirstName: rule!AS_CO_UT_replaceNull(
            nullableValue: index(ri!firstName, fv!index, {}),
            replacementValue: user(fv!item, "firstName")
          ),
          local!userLastName: rule!AS_CO_UT_replaceNull(
            nullableValue: index(ri!lastName, fv!index, {}),
            replacementValue: user(fv!item, "lastName")
          ),
          local!userPhoneNumber: user(fv!item, "phoneMobile"),
          local!userEmail: rule!AS_CO_UT_replaceNull(
            nullableValue: index(ri!emails, fv!index, {}),
            replacementValue: user(fv!item, "email")
          ),
          if(
            /*User exist in ita_user?*/
            rule!AS_CO_UT_isNotBlank(local!filteredUser),
            /*Match user properties against DB values*/
            if(
              and(
                local!filteredUser.firstName = local!userFirstName,
                local!filteredUser.lastName = local!userLastName,
                local!filteredUser.phoneNumber = local!userPhoneNumber,
                local!filteredUser.email = local!userEmail,
                local!filteredUser.isActive = local!userStatus
              ),
              /*Return nothing - full happy path*/
              {},
              /*Update attributes*/
              rule!AS_CO_UT_updateCdtFields(
                cdt: local!filteredUser,
                fields: {
                  "firstName",
                  "lastName",
                  "modifiedDatetime",
                  "isActive",
                  "phoneNumber",
                  "email",
                  "modifiedBy"
                },
                newValues: {
                  local!userFirstName,
                  local!userLastName,
                  now(),
                  local!userStatus,
                  local!userPhoneNumber,
                  local!userEmail,
                  "appian.administrator"
                }
              )
            ),
            /*Create a new user*/
            cast(
              ri!entityMap.entityType,
              {
                username: fv!item,
                firstName: local!userFirstName,
                lastName: local!userLastName,
                phoneNumber: local!userPhoneNumber,
                createdDatetime: now(),
                modifiedDatetime: now(),
                isActive: local!userStatus,
                email: local!userEmail,
                createdBy: "appian.administrator",
                modifiedBy: "appian.administrator"
              }
            )
          )
        )
      )
    ),
    local!distinctUsers: rule!AS_CO_UT_cdtDistinctOnField(cdt: local!usersForDb, field: "username"),
    rule!AS_CO_UT_castToMapList(
      value: reject(
        rule!AS_CO_UT_isBlank(_),
        local!distinctUsers
      )
    )
  )
)

