  with(
    local!modifiedBy: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!modifiedBy, 
      replacementValue: loggedInUser()
    ),
    local!cdtFields: rule!AS_CO_UT_returnCdtFields(typeOf: ri!cdt),
    local!possibleMetaDataFields: touniformstring(
      /*These fields should always exist in any CDT for which metadata is captured*/
      "modifiedDatetime",
      "modifiedBy",
      "createdDatetime",
      "createdBy",
      /*These fields may not exist in every CDT for which metadata is captured*/
      "isDeleted",
      "isActive"
