_a-0000eb7c-0389-8000-9d2c-011c48011c48_.sail_code:
with(
  local!listOneFlattened: a!flatten(ri!listOne),
  local!listTwoFlattenedAndCast: cast(
    runtimetypeof(local!listOneFlattened),
    a!flatten(ri!listTwo)
  ),
  and(
    a!flatten(
      {
        if(
          ri!lengthMatters,
          count(local!listOneFlattened) = count(local!listTwoFlattenedAndCast),
          true
        ),
        if(
          ri!orderMatters,
          local!listOneFlattened = local!listTwoFlattenedAndCast,
          {
            a!isNullOrEmpty(
              difference(
                local!listOneFlattened,
                local!listTwoFlattenedAndCast
              )
            ),
            a!isNullOrEmpty(
              difference(
                local!listTwoFlattenedAndCast,
                local!listOneFlattened
              )
            )
          }
        )
      }
    )
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "originalObjects",
      value: rule!AS_CO_UT_replaceNull(
        nullableValue: a!flatten(fv!item.originalObjects),
        replacementValue: if(
          /*Initializing null when the objectDse is null*/
          rule!AS_CO_UT_isBlank(fv!item.objectDse),
          rule!AS_CO_UT_initializeBlankLocalVariable(
            type: a!flatten(fv!item).objectTypeNumber
          ),
          if(
            rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
            if(
              /*Initializing null when the idField in the audit config is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  a!flatten(fv!item.auditConfig),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(auditParams: fv!item),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            ),
            if(
              /*Initializing null when idField of complexField's auditConfig is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(fv!item.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              /*Getting the original object for the complexFieldName object*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(
                  auditParams: rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "auditConfig",
                    newValues: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.auditConfig.complexFields,
                      field: "auditFieldName",
                      value: tostring(fv!item.complexFieldName)
                    )
                  )
                ),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            )
          )
        )
      )
    }
  )
)

_a-0000ea13-fcbd-8000-9c86-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS_GSS_Criteria_SYNCEDRECORD,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId,
      operator: "in",
      value: ri!criteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluationId,
      operator: "in",
      value: ri!evaluationId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
      operator: a!match(
        value: ri!isParent,
        equals: true,
        then: "is null",
        equals: false,
        then: "not null",
        default: "in"
      ),
      value: ri!parentCriteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
      operator: "in",
      value: a!defaultValue(
        value: ri!isActive,
        default: {true}
      )
    ),
    ri!additionalfilters
  },
  relatedRecordData: ri!relatedRecordData,
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.EvaluatorTeam,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluation,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria
        },
        {}
      )
    }
  ),
  sort: ri!sortInfo,
  pagingInfo: ri!pagingInfo,
  returnType: ri!returnType,
  executeWhen: ri!executeWhen,
  triggerRefresh: ri!triggerRefresh,
  logicalExpression: ri!logicalExpression,
  aggregationFields: ri!aggregationFields
)

_a-0000e40a-de32-8000-9ba8-011c48011c48_.sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Integer?list',
  with(
    local!array: a!flatten(ri!array),

    if(

      /*Have to handle separately for document types due to how it casts to string*/
      rule!AS_CO_UT_typeCompare(
        value1: local!array,
        value2: {todocument(null)}
      ),
      wherecontains(
        todocument(ri!values),
        local!array
      ),

      /*Have to handle separately for group types due to how it casts to string*/
      rule!AS_CO_UT_typeCompare(
        value1: local!array,
        value2: {togroup(null)}
      ),
      wherecontains(
        togroup(ri!values),
        local!array
      ),

      /*Casting to string accounts for any primitive type (integer, boolean, ect.)*/
      wherecontains(
        if(
          rule!AS_CO_UT_isBlank(ri!values),
          touniformstring(ri!values),
          touniformstring(a!flatten(ri!values))
        ),
        touniformstring(local!array)
      )
    )
  )
)

