      cdt: local!newEvaluationDocs,
      field: "docType.refDataId",
      value: cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
    )
  },
  rule!AS_CO_UT_rejectNullValuesFromArray(
    array: {
      /*Field Changes Construction for vendor docs*/
      if(
        rule!AS_CO_UT_isBlank(local!groupedDocs.vendorDocs),
        null,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!groupedDocs.vendorDocs,
          expression: rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:ADT}AS_ADT_P_ManipulationAudit',
