              actionCode: rule!AS_CO_UT_replaceNull(
                nullableValue: ri!externalAction,
                replacementValue: cons!AS_GAM_ENUM_AUDIT_ACTION_CODE_UPLOAD
              ),
              fieldChanges: {
                {
                  fieldName: "documentsCount",
                  oldValue: null,
                  newValue: 1
                },
                {
                  fieldName: "evalDocumentId",
                  oldValue: null,
                  newValue: fv!item.evalDocumentId
                },
                {
                  fieldName: "docType",
                  oldValue: null,
                  newValue: cons!AS_GSS_REF_LABEL_DOC_TYPE_VENDOR
                },
                {
                  fieldName: "vendorId",
                  oldValue: null,
                  newValue: fv!item.vendorId
                },
