      if(
        rule!AS_CO_UT_isBlank(local!groupedDocs.referenceDocs),
        null,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!groupedDocs.referenceDocs,
          expression: rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:ADT}AS_ADT_P_ManipulationAudit',
            value: {
              actionCode: rule!AS_CO_UT_replaceNull(
                nullableValue: ri!externalAction,
                replacementValue: cons!AS_GAM_ENUM_AUDIT_ACTION_CODE_UPLOAD
              ),
              fieldChanges: rule!AS_CO_UT_castToDictionaryList(
                value: {
                  {
                    fieldName: "documentName",
                    oldValue: null,
                    newValue: fv!item.documentName
                  },
                  {
                    fieldName: "docType",
                    oldValue: null,
                    newValue: cons!AS_GSS_REF_LABEL_DOC_TYPE_REFERENCE
                  }
                }
              )
            }
          )
        )
      )
    }
  )
)

