_a-0000ecda-a664-8000-9dc8-011c48011c48_.sail_code:
a!localVariables(
  local!evaluationDetails: rule!AS_CO_UT_replaceNull(
    nullableValue: ri!evaluation,
    replacementValue: rule!AS_GSS_QR_getEvaluation(
      executeWhen: a!isNotNullOrEmpty(ri!evaluationId),
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
      evaluationId: ri!evaluationId
    )
  ),
  local!externalAction: if(
    rule!AS_CO_UT_booleanDefaultFalse(boolean: ri!isDuplicationEvent),
    cons!AS_GSS_ADT_ENUM_AUDIT_ACTION_CODE_DUPLICATED,
    rule!AS_CO_UT_booleanDefaultFalse(
      boolean: ri!isCreateFromSolicitationEvent
    ),
    cons!AS_GSS_ADT_ENUM_AUDIT_ACTION_CODE_CREATED_FROM_SOLICITATION,
    if(
      rule!AS_CO_UT_isBlank(
        value: ri!originalEvaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
      ),
      cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
      cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY
    )
  ),
  local!updatedOriginalEvaluation: rule!AS_GSS_UT_convertEvaluationRecordDataToDictionaryForAudit(evaluation: ri!originalEvaluation),
  local!updatedEvaluation: rule!AS_GSS_UT_convertEvaluationRecordDataToDictionaryForAudit(evaluation: ri!evaluation),
  local!updatedOrigEvalFactors: #"SYSTEM_SYSRULES_forEach"(
    items: ri!originalEvalFactors,
    expression: rule!AS_GSS_UT_convertFactorRecordDataToDictionaryForAudit(
      evalFactors: fv!item,
      evaluation: ri!originalEvaluation
    )
  ),
  local!updatedEvalFactors: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evalFactors,
    expression: rule!AS_GSS_UT_convertFactorRecordDataToDictionaryForAudit(
      evalFactors: fv!item,
      evaluation: ri!evaluation
    )
  ),
  local!updatedOrigEvalSubFactors: rule!AS_CO_UT_rejectNullValuesFromArray(
    array: #"SYSTEM_SYSRULES_forEach"(
      items: a!flatten(
        index(
          ri!originalEvalFactors,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria,
          {}
        )
      ),
      expression: rule!AS_GSS_UT_convertFactorRecordDataToDictionaryForAudit(
        evalFactors: fv!item,
        evaluation: ri!originalEvaluation
      )
    )
  ),
  local!updatedEvalSubFactors: rule!AS_CO_UT_rejectNullValuesFromArray(
    array: #"SYSTEM_SYSRULES_forEach"(
      items: a!flatten(
        index(
          ri!evalFactors,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria,
          {}
        )
      ),
      expression: rule!AS_GSS_UT_convertFactorRecordDataToDictionaryForAudit(
        evalFactors: fv!item,
        evaluation: ri!evaluation
      )
    )
  ),
  local!updatedOrigEvalPhases: #"SYSTEM_SYSRULES_forEach"(
    items: ri!originalEvalPhases,
    expression: rule!AS_GSS_UT_convertPhaseRecordDataToDictionaryForAudit(phase: fv!item, evaluation: ri!evaluation)
  ),
  local!updatedEvalPhases: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evalPhases,
    expression: rule!AS_GSS_UT_convertPhaseRecordDataToDictionaryForAudit(phase: fv!item, evaluation: ri!evaluation)
  ),
  local!updatedOrigEvalTeams: #"SYSTEM_SYSRULES_forEach"(
    items: ri!originalEvalTeams,
    expression: rule!AS_GSS_UT_convertTeamRecordDataToDictionaryForAudit(team: fv!item, evaluation: ri!evaluation)
  ),
  local!updatedEvalTeams: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evalTeams,
    expression: rule!AS_GSS_UT_convertTeamRecordDataToDictionaryForAudit(team: fv!item, evaluation: ri!evaluation)
  ),
  local!updatedOrigRefDocs: #"SYSTEM_SYSRULES_forEach"(
    items: ri!originalEvalDocs,
    expression: rule!AS_GSS_UT_convertDocumentRecordDataToDictionaryForAudit(
      document: fv!item,
      evaluation: local!evaluationDetails
    )
  ),
  local!updatedRefDocs: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evalDocs,
    expression: rule!AS_GSS_UT_convertDocumentRecordDataToDictionaryForAudit(
      document: fv!item,
      evaluation: local!evaluationDetails
    )
  ),
  local!updatedEvalVendors: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evalVendors,
    expression: rule!AS_GSS_UT_convertVendorRecordDataToDictionaryForAudit(
      vendor: fv!item,
      evaluation: local!evaluationDetails
    )
  ),
  local!updatedOriginalEvalVendors: #"SYSTEM_SYSRULES_forEach"(
    items: ri!originalEvalVendors,
    expression: rule!AS_GSS_UT_convertVendorRecordDataToDictionaryForAudit(
      vendor: fv!item,
      evaluation: local!evaluationDetails
    )
  ),
  local!addedDocs: rule!AS_CO_UT_indexWhere(
    value: false(),
    initialArray: local!updatedRefDocs.isDeleted,
    finalArray: local!updatedRefDocs,
    isRemove: false()
  ),
  local!removedDocs: rule!AS_CO_UT_indexWhere(
    value: true(),
    initialArray: local!updatedRefDocs.isDeleted,
    finalArray: local!updatedRefDocs,
    isRemove: false()
  ),
  /* The below local! are related to capturing audit when there is */
  /* a change in the factor mapping for a document */
  local!updatedOriginalFacDocMappings: rule!AS_GSS_UT_convertFactorDocMappingRecordDataToDictionaryForAudit(
    factorDocumentMapping: ri!originalFactorDocMapping
  ),
  local!factorDocMapping: if(
    a!isNullOrEmpty(
      value: index(local!addedDocs, "evalDocumentId", null)
    ),
    {},
    rule!AS_GSS_QR_getFactorDocumentMapping(
      evaluationDocumentId: local!addedDocs.evalDocumentId,
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
    )
  ),
  local!updatedFactorDocMapping: rule!AS_GSS_UT_convertFactorDocMappingRecordDataToDictionaryForAudit(
    factorDocumentMapping: local!factorDocMapping
  ),
  /* We are constructing the maipulation audit here as we have to verify */
  /* if any mappings has been modified or not. This will be done only for the */
  /* documents which has been already uploaded to the vendor. */
  local!manAuditForMappingChanges: rule!AS_GSS_UT_constructManpAuditForFactorDocMappingChanges(
    addedDocs: local!addedDocs,
    updatedFactorDocMapping: local!updatedFactorDocMapping,
    updatedOriginalFacDocMappings: local!updatedOriginalFacDocMappings,
    originalEvalDocs: ri!originalEvalDocs
  ),
  rule!AS_CO_UT_rejectNullValuesFromArray(
    array: {
      if(
        rule!AS_CO_UT_isBlank(value: local!updatedEvaluation),
        {},
        {
          rule!AS_ADT_UT_startAuditProcess_Parameters(
            originalObjects: local!updatedOriginalEvaluation,
            newObjects: local!updatedEvaluation,
            objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
            auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
            auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
            username: ri!initiator,
            auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_FIELD_LEVEL,
            externalAction: local!externalAction
          ).auditParams
        }
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!updatedEvalFactors),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          originalObjects: local!updatedOrigEvalFactors,
          newObjects: local!updatedEvalFactors,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          externalAction: local!externalAction,
          complexFieldName: "criteriaChanges",
          manipulationAudit: {}
        ).auditParams
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!updatedEvalSubFactors),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          originalObjects: local!updatedOrigEvalSubFactors,
          newObjects: local!updatedEvalSubFactors,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          externalAction: local!externalAction,
          complexFieldName: "criteriaChanges",
          manipulationAudit: {}
        ).auditParams
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!updatedEvalPhases),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          originalObjects: local!updatedOrigEvalPhases,
          newObjects: local!updatedEvalPhases,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          externalAction: local!externalAction,
          complexFieldName: "evaluationPhaseChanges",
          manipulationAudit: {}
        ).auditParams
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!updatedEvalTeams),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          originalObjects: local!updatedOrigEvalTeams,
          newObjects: local!updatedEvalTeams,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          externalAction: local!externalAction,
          complexFieldName: "evaluatorTeamChanges",
          manipulationAudit: {}
        ).auditParams
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!addedDocs),
        {},
        rule!AS_GSS_UT_evalDocsStartAuditProcess_Parameters(
          evaluationDocuments: local!addedDocs,
          originalEvalDocuments: rule!AS_CO_UT_indexWhere(
            value: local!addedDocs.evalDocumentId,
            initialArray: local!updatedOrigRefDocs.evalDocumentId,
            finalArray: local!updatedOrigRefDocs,
            isRemove: false()
          ),
          initiator: ri!initiator,
          writeObject: false(),
          isDeleteDocument: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!isDeleteDocument,
            replacementValue: false()
          ),
          factorDocMapping: local!updatedFactorDocMapping
        )
      ),
      if(
        rule!AS_CO_UT_isBlank(value: local!removedDocs),
        {},
        rule!AS_GSS_UT_evalDocsStartAuditProcess_Parameters(
          evaluationDocuments: local!removedDocs,
          originalEvalDocuments: {},
          initiator: ri!initiator,
          writeObject: false(),
          isDeleteDocument: rule!AS_CO_UT_replaceNull(
            nullableValue: ri!isDeleteDocument,
            replacementValue: true()
          ),
          factorDocMapping: rule!AS_GSS_UT_convertFactorDocMappingRecordDataToDictionaryForAudit(
            factorDocumentMapping: rule!AS_GSS_QR_getFactorDocumentMapping(
              evaluationDocumentId: local!removedDocs.evalDocumentId,
              returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
            )
          )
        )
      ),
      /* This below config is to capture the audit for any changes in the factor */
      /* document mappings. */
      if(
        rule!AS_CO_UT_checkIsNullOrEmpty(
          value: index(local!manAuditForMappingChanges,"actionCode",null)
        ),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          newObjects: local!addedDocs,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          externalAction: cons!AS_GSS_ENUM_AUDIT_ACTION_CODE_DOC_UPDATE,
          complexFieldName: "documentChanges",
          manipulationAudit: local!manAuditForMappingChanges
        ).auditParams
      ),
      if(
        rule!AS_CO_UT_isBlank(value: ri!evalVendors),
        {},
        rule!AS_ADT_UT_startAuditProcess_Parameters(
          originalObjects: local!updatedOriginalEvalVendors,
          newObjects: local!updatedEvalVendors,
          objectType: 'type!{http://www.appian.com/ae/types/2009}Dictionary',
          auditDse: cons!AS_GSS_ENT_A_R_EVALUATION,
          auditConfig: rule!AS_GSS_ADT_BL_auditConfig_Evaluation(),
          username: ri!initiator,
          auditType: cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
          /*Providing external action as the audit Type is 'Manipulation*/
          externalAction: local!externalAction,
          complexFieldName: "vendorChanges",
          /*Nested field name in AS_GSS_A_R_Evaluation*/
          manipulationAudit: {}
        ).auditParams
      )
    }
  )
)

