_a-0000eee8-b1ee-8000-9e5a-011c48011c48_.sail_code:
a!map(
  saveAndClose: a!map(
    disabled: any(
      rule!AS_GSS_BL_doesInputExceedsAllowedMaxLength(
        input: _,
        maxLength: cons!AS_GSS_INT_VENDOR_DECISION_REASON_TEXT_SIZE
      ),
      index(
        ri!allEvaluationVendors,
        recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionReason,
        { null }
      )
    ),
    showWhen: true()
  ),
  cancel: a!map(
    disabled: and(
      ri!pageConfig.isFirstVendor,
      ri!pageConfig.currentVendorIndex <> ri!pageConfig.lastViewedVendorIndex
    ),
    showWhen: ri!pageConfig.isFirstVendor
  ),
  back: a!map(
    disabled: false(),
    showWhen: not(ri!pageConfig.isFirstVendor)
  ),
  next: if(
    ri!isSingleAwardType,
    /* 1. Should show when the current vendor is not the last vendor */
    /* 2. Should be disabled when decision is not empty or the current vendor is selected */
    a!map(
      disabled: or(
        rule!AS_CO_UT_checkIsNullOrEmpty(
          value: index(
            index(
              ri!allEvaluationVendors,
              ri!pageConfig.currentVendorIndex,
              null
            ),
            recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
            null
          )
        ),
        tointeger(
          ri!currentEvaluationvendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
        ) = cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT
      ),
      showWhen: not(ri!pageConfig.isLastVendor)
    ),
    /* 1. Should show when the current vendor is not the last vendor */
    /* 2. Should be disabled if the decision is empty for the vendor */
    a!map(
      disabled: rule!AS_CO_UT_checkIsNullOrEmpty(
        value: index(
          index(
            ri!allEvaluationVendors,
            ri!pageConfig.currentVendorIndex,
            null
          ),
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
          null
        )
      ),
      showWhen: not(ri!pageConfig.isLastVendor)
    )
  ),
  submit: if(
    ri!isSingleAwardType,
    /* 1. Should be disabled if no vendor is selected */
    a!map(
      disabled: rule!AS_CO_UT_checkIsNullOrEmpty(
        value: rule!AS_CO_UT_indexWhere(
          value: cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT,
          initialArray: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId],
          finalArray: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId],
          isRemove: false()
        )
      ),
      showWhen: true()
    ),
    /* 1. Should be disabled if no vendor is selected */
    a!map(
      disabled: rule!AS_CO_UT_checkIsNullOrEmpty(
        value: rule!AS_CO_UT_indexWhere(
          value: cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT,
          initialArray: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId],
          finalArray: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId],
          isRemove: false()
        )
      ),
      showWhen: true()
    )
  )
)

