a!localVariables(
  local!vendorCount: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!allEvaluationVendors),
    0,
    length(ri!allEvaluationVendors)
  ),
  local!vendorIndices: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!allEvaluationVendors),
    {},
    enumerate(local!vendorCount) + 1
  ),
  /*  In case of single award, the last rejected vendor will be the last edited  */
  /*  In case of multiple, the last vendor for which the decision is taken will be the last edited  */
  local!lastEditedVendorByDecision: if(
    ri!isSingleAwardType,
    max(
      rule!AS_CO_UT_whereContains(
        values: cons!AS_GSS_REF_ID_VENDOR_DECISION_REJECT,
        array: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
      ),
      1
    ),
    max(
      rule!AS_CO_UT_whereContains(
        values: cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT,
        array: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
      ),
      rule!AS_CO_UT_whereContains(
        values: cons!AS_GSS_REF_ID_VENDOR_DECISION_REJECT,
        array: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
      ),
      1
    )
  ),
  /*  In case of multi award, we dont need this already selected vendor info  */
  local!alreadySelectedVendor: if(
    ri!isSingleAwardType,
    rule!AS_CO_UT_whereContains(
      values: cons!AS_GSS_REF_ID_VENDOR_DECISION_SELECT,
      array: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
    ),
    null
  ),
  local!lastSavedVendor: rule!AS_CO_UT_whereContains(
    values: cons!AS_GSS_REF_ID_VENDOR_DECISION_UNDER_REVIEW,
    array: ri!allEvaluationVendors[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
  ),
  /*  If the user has already selected a vendor or saved and closed a vendor  */
  /*  the user should land on that vendor */
  local!lastEditedVendorIndex: rule!AS_CO_UT_replaceNull(
    nullableValue: local!alreadySelectedVendor,
    replacementValue: rule!AS_CO_UT_replaceNull(
      nullableValue: local!lastSavedVendor,
      replacementValue: local!lastEditedVendorByDecision
    )
  ),
  /*  If currentVendorIndex is null, it denotes the user is opening a saved and closed form.  */
  /*  So we take the user to the last edited vendor.  */
  local!currentVendorIndex: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!pageConfig.currentVendorIndex),
    local!lastEditedVendorIndex,
    if(
      rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!isNextAction),
      ri!pageConfig.currentVendorIndex,
      if(
        ri!isNextAction,
        ri!pageConfig.currentVendorIndex + 1,
        ri!pageConfig.currentVendorIndex - 1
      )
    )
  ),
  local!lastViewedVendorIndex: if(
    rule!AS_CO_UT_checkIsNullOrEmpty(
      value: ri!pageConfig.lastViewedVendorIndex
    ),
    local!lastEditedVendorIndex,
    if(
      ri!isNextAction,
      max(
        ri!pageConfig.lastViewedVendorIndex,
        local!currentVendorIndex
      ),
      ri!pageConfig.lastViewedVendorIndex
    )
  ),
  a!map(
    /*  Holds the indicies of all vendors  */
    vendorIndices: local!vendorIndices,
    /*  Holds the index of the vendor which is currently being viewed  */
    currentVendorIndex: tointeger(local!currentVendorIndex),
    /*  Holds the index of the vendor last viewed by the user  */
    lastViewedVendorIndex: local!lastViewedVendorIndex,
    /*  Indicator if the crrent vendor is the first one in list  */
    isFirstVendor: tointeger(local!currentVendorIndex) = 1,
    /*  Indicator if the crrent vendor is the last one in list  */
    isLastVendor: local!vendorCount = tointeger(local!currentVendorIndex)
  )
)

