if(
  a!isNullOrEmpty(
    value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
  ),
  ri!evaluation,
  a!localVariables(
    local!vendorInfo: rule!AS_GSS_QR_getEvaluationVendors(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      fields: rule!AS_CO_UT_rejectNullValuesFromArray(
        array: {
          ri!vendorFields
        }
      ),
      executeWhen: ri!executeWhen,
      evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
      triggerRefresh: ri!triggerRefresh,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: a!defaultValue(
          value: ri!batchSize,
          default: cons!AS_GSS_INT_QUERY_RECORD_MAX_BATCH_SIZE
        ),
        sort: a!defaultValue(
          value: ri!sort,
          default: {
            #"SYSTEM_SYSRULES_sortInfo"(
              field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.pendingUpdateCount,
              ascending: false()
            ),
            #"SYSTEM_SYSRULES_sortInfo"(
              field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
              ascending: true()
            ),
            #"SYSTEM_SYSRULES_sortInfo"(
              field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.sortableTotalPriceValue,
              ascending: true()
            ),
            #"SYSTEM_SYSRULES_sortInfo"(
              field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.submittedDate,
              ascending: false
            ),
            #"SYSTEM_SYSRULES_sortInfo"(
              field: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.legalName,
              ascending: true()
            )
          }
        )
      )
    ).data,
    local!vendorInfoWithPriceBreakup: if(
      ri!includePriceBreakup = true(),
      rule!AS_GSS_UT_updateEvalVendorRecordWithAllPriceBreakups(
        evalVendor: local!vendorInfo,
        triggerRefresh: ri!triggerRefresh,
        executeWhen: ri!executeWhen,
        batchSize: ri!batchSize
      ),
      local!vendorInfo
    ),
    rule!AS_CO_UT_cast(
      typeOf: ri!evaluation,
      value: rule!AS_GSS_UT_updateRecordsByModelRecord(
        records: ri!evaluation,
        modelRecord: recordType!AS_GSS_Evaluation_SYNCEDRECORD(
          recordType!AS_GSS_Evaluation_SYNCEDRECORD.vendor: local!vendorInfoWithPriceBreakup
        )
      )
    )
  )
)

_a-0000ee0b-274d-8000-9e1b-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_ButtonLayout"(
  primaryButtons: {
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Save and Close",
      submit: true(),
      value: cons!AS_CO_ENUM_USER_ACTION_SAVE,
      saveInto: { ri!buttonAction },
      style: "OUTLINE",
      showWhen: true(),
      validate:false(),
      disabled: any(
        rule!AS_GSS_BL_doesInputExceedsAllowedMaxLength(
          input: _,
          maxLength: cons!AS_GSS_INT_VENDOR_DECISION_REASON_TEXT_SIZE
        ),
        index(
          ri!allEvaluationVendors,
          recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionReason,
          { null }
        )
      )
    ),
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Next",
      saveInto: {
        a!save(
          ri!pageConfig,
          rule!AS_GSS_updatePagingConfigurationForLptaEvaluation(
            isNextAction: true(),
            pageConfig: ri!pageConfig,
            allEvaluationVendors: ri!allEvaluationVendors
          )
        ),
        if(
          rule!AS_CO_UT_checkIsNullOrEmpty(
            value: index(
              ri!allEvaluationVendors[ri!pageConfig.currentVendorIndex],
              recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
              null
            )
          ),
          a!save(
            ri!allEvaluationVendors[ri!pageConfig.currentVendorIndex],
            #"urn:appian:function:v1:a:update"(
              ri!allEvaluationVendors[ri!pageConfig.currentVendorIndex],
              recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId,
              cons!AS_GSS_REF_ID_VENDOR_DECISION_UNDER_REVIEW
            )
          ),
          {}
        )
      },
      style: "SOLID",
      disabled: false(),
      showWhen: and(
        tointeger(
          ri!currentEvaluationvendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
        ) = cons!AS_GSS_REF_ID_VENDOR_DECISION_REJECT,
        not(ri!pageConfig.isLastVendor)
      ),
      validate: true()
    ),
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Submit",
      submit: true(),
      value: cons!AS_CO_ENUM_USER_ACTION_SUBMIT,
      saveInto: {
        ri!buttonAction,
        a!save(
          ri!allEvaluationVendors,
          rule!AS_GSS_UT_updateVendorDecisionForDeferredVendors(
            evaluationVendors: ri!allEvaluationVendors
          )
        )
      },
      style: "SOLID",
      disabled: or(
        rule!AS_CO_UT_checkIsNullOrEmpty(
          value: ri!currentEvaluationvendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
        ),
        tointeger(
          ri!currentEvaluationvendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
        ) = cons!AS_GSS_REF_ID_VENDOR_DECISION_UNDER_REVIEW
      ),
      showWhen: or(
        tointeger(
          ri!currentEvaluationvendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.decisionTypeId]
        ) <> cons!AS_GSS_REF_ID_VENDOR_DECISION_REJECT,
        ri!pageConfig.isLastVendor
      ),
      validate: true()
    )
  },
  secondaryButtons: {
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Cancel",
      value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
      saveInto: { ri!buttonAction },
      style: "OUTLINE",
      color: "SECONDARY",
      disabled: and(
        ri!pageConfig.isFirstVendor,
        ri!pageConfig.currentVendorIndex <> ri!pageConfig.lastViewedVendorIndex
      ),
      submit: true(),
      validate: false(),
      showWhen: ri!pageConfig.isFirstVendor
    ),
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Back",
      saveInto: {
        a!save(
          ri!pageConfig,
          rule!AS_GSS_updatePagingConfigurationForLptaEvaluation(
            isNextAction: false(),
            pageConfig: ri!pageConfig,
            allEvaluationVendors: ri!allEvaluationVendors
          )
        )
      },
      style: "OUTLINE",
      disabled: false(),
      showWhen: not(ri!pageConfig.isFirstVendor)
    )
  }
)

