if(
  a!isNullOrEmpty(
    value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
  ),
  a!map(
    vendorDocuments: {},
    referenceDocuments: {},
    factorDocuments: {}
  ),
  a!localVariables(
    local!defaultFactor: rule!AS_GSS_QR_getCriteria(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
      evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
      additionalFilters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isDefaultEntry,
          operator: "=",
          value: true()
        )
      }
    ),
    local!activeFactorMappings: if(
      rule!AS_CO_UT_checkIsNullOrEmpty(
        value: local!defaultFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
      ),
      {},
      rule!AS_GSS_QR_getFactorDocumentMapping(
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        additionalfilters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.factorId,
          operator: "in",
          value: {
            local!defaultFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
          }
        ),
        isActive: true()
      )
    ),
    local!allDocs: rule!AS_GSS_QR_getEvaluationDocuments(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      evaluationId: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId],
      isDeleted: false(),
      logicalExpression: {
        #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "OR",
          filters: {},
          logicalExpressions: {
            /*Reference Docs*/
            #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              filters: {
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationId,
                  operator: "=",
                  value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
                ),
                #"SYSTEM_SYSRULES_queryFilter"(
                  field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
                  operator: "in",
                  value: { cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE }
                )
              }
            ),
            /*Vendor Docs*/
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(
                value: local!defaultFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
              ),
              {},
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorId,
                    operator: "in",
                    value: {
                      ri!vendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId]
                    }
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId,
                    operator: "in",
                    value: if(
                      a!isNullOrEmpty(local!activeFactorMappings),
                      { 0 },
                      local!activeFactorMappings[recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentId]
                    )
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
                    operator: "in",
                    value: { cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR }
                  )
                }
              )
            ),
            /*Factor Docs*/
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(
                value: local!defaultFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
              ),
              {},
              #"SYSTEM_SYSRULES_queryLogicalExpression"(
                operator: "AND",
                filters: {
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationId,
                    operator: "=",
                    value: ri!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId]
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.criteriaId,
                    operator: "in",
                    value: {
                      local!defaultFactor[recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId]
                    }
                  ),
                  #"SYSTEM_SYSRULES_queryFilter"(
                    field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
                    operator: "in",
                    value: { cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR }
                  )
                }
              )
            )
          }
        )
      }
    ),
    local!vendorDocuments: rule!AS_CO_UT_filterRecordByFieldValues(
      records: rule!AS_CO_UT_filterRecordByFieldValues(
        records: local!allDocs,
        field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorId,
        values: ri!vendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.vendorId]
      ),
      field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
      values: cons!AS_GSS_REF_ID_DOC_TYPE_VENDOR
    ),
    local!referenceDocuments: rule!AS_CO_UT_filterRecordByFieldValues(
      records: local!allDocs,
      field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
      values: cons!AS_GSS_REF_ID_DOC_TYPE_REFERENCE
    ),
    local!factorDocuments: rule!AS_CO_UT_filterRecordByFieldValues(
      records: local!allDocs,
      field: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.docType,
      values: cons!AS_GSS_REF_ID_DOC_TYPE_FACTOR
    ),
    a!map(
      vendorDocuments: a!map(
        label: rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!bundle,
          bundleKey: "lbl_VendorDocuments"
        ),
        documents: local!vendorDocuments,
        showWhen: a!isNotNullOrEmpty(local!vendorDocuments)
      ),
      referenceDocuments: a!map(
        label: rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!bundle,
          bundleKey: "lbl_ReferenceDocuments"
        ),
        documents: local!referenceDocuments,
        showWhen: a!isNotNullOrEmpty(local!referenceDocuments)
      ),
      factorDocuments: a!map(
        label: rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!bundle,
          bundleKey: "lbl_FactorOrSubfactorDocuments"
        ),
        documents: local!factorDocuments,
        showWhen: a!isNotNullOrEmpty(local!factorDocuments)
      )
    )
  )
)

