_a-0000ecda-a664-8000-9dc8-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!data),
  ri!data,
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!data,
    expression: rule!AS_GSS_UT_updateRecordsByModelRecord(
      records: fv!item,
      modelRecord: cast(
        ri!recordType,
        #"urn:appian:function:v1:a:update"(
          fv!item,
          {
            if(
              a!isNullOrEmpty(value: ri!modifiedByField),
              {},
              ri!modifiedByField
            ),
            if(
              a!isNullOrEmpty(value: ri!modifiedOnField),
              {},
              ri!modifiedOnField
            ),
            ri!createdByField,
            ri!createdOnField,
            if(
              a!isNullOrEmpty(value: ri!isActiveField),
              {},
              ri!isActiveField
            ),
            if(
              a!isNullOrEmpty(value: ri!isDeletedField),
              {},
              ri!isDeletedField
            )
          },
          {
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!modifiedByField),
              {},
              rule!AS_CO_UT_replaceNull(
                nullableValue: ri!modifiedBy,
                replacementValue: loggedInUser()
              )
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!modifiedOnField),
              {},
              now()
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item[ri!createdByField],
              replacementValue: rule!AS_CO_UT_replaceNull(
                nullableValue: ri!modifiedBy,
                replacementValue: loggedInUser()
              )
            ),
            rule!AS_CO_UT_replaceNull(
              nullableValue: fv!item[ri!createdOnField],
              replacementValue: now()
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!isActiveField),
              {},
              rule!AS_CO_UT_booleanDefaultTrue(boolean: fv!item[ri!isActiveField])
            ),
            if(
              rule!AS_CO_UT_checkIsNullOrEmpty(value: ri!isDeletedField),
              {},
              rule!AS_CO_UT_booleanDefaultFalse(boolean: fv!item[ri!isDeletedField])
            )
          }
        )
      )
    )
  )
)

