_a-0000eb16-753f-8000-9d01-011c48011c48_.sail_code:
/*No assertion added as the data will not be consistent in test environments*/
a!localVariables(
  local!consensusReport: rule!AS_GSS_QE_getConsensusReport(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    consensusId: ri!consensusId,
    isArchived: false,
    status: cons!AS_GSS_REF_ID_CONSENSUS_REPORT_STATUS_AWAITING_SIGNATURE,
    triggerRefresh: ri!triggerRefresh
  ),
  local!isSignatureRequired: rule!AS_GSS_BL_isConsensusReportSignaturesRequired(
    evaluationId: local!consensusReport.evaluationId
  ),
  local!factor: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    criteriaId: local!consensusReport.factorId,
    executeWhen: local!isSignatureRequired
  ),
  and(
    /*Checking if the signature is required*/
    local!isSignatureRequired,
    /*Checking if user is part of Criteria Chair/Evaluators of 
    the concerned factor*/
    contains(
      rule!AS_CO_UT_rejectNullValuesFromArray(
        touser(
          {
            /*Criteria Chair*/
            local!factor.criteriaChair.username,
            /*Evaluators*/
            rule!AS_CO_UT_filterCdtByField(
              cdt: local!factor.assignments,
              field: "isActive",
              value: true
            ).assignee.username
          }
        )
      ),
      ri!user
    ),
    /*Checking if user has already signed*/
    a!isNotNullOrEmpty(
      rule!AS_GSS_QR_getConsensusSignatures(
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
        username: ri!user,
        consensusId: ri!consensusId,
        isActive: true,
        isUnsigned: true
      )
    )
  )
)

