_a-0000eb1f-ba4f-8000-9d06-011c48011c48_.sail_code:
a!localVariables(
  local!bundle: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!evaluation: rule!AS_GSS_QR_getEvaluation(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    evaluationId: ri!consensusReport.evaluationId,
    fields: {
      recordType!AS_GSS_Evaluation_SYNCEDRECORD.evaluationId,
      recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked
    }
  ),
  local!evaluatorNickNames: rule!AS_GSS_QR_getMaskEvaluatorDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    evaluationId: ri!consensusReport.evaluationId
  ),
  local!consensusSignatures: rule!AS_GSS_QR_getConsensusSignatures(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    consensusId: ri!consensusReport.consensusId,
    isUnsigned: false
  ),
  local!factor: rule!AS_GSS_QE_getEvaluationCriteria(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    criteriaId: ri!consensusReport.factorId
  ),
  local!evaluators: touniformstring(
    rule!AS_CO_UT_filterCdtByField(
      cdt: local!factor.assignments,
      field: "isActive",
      value: true
    ).assignee.username
  ),
  local!factorChair: tostring(local!factor.criteriaChair.username),
  local!userDetails: rule!AS_GSS_QE_getUser(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    usernames: touniformstring({ local!evaluators, local!factorChair })
  ),
  local!dynamicLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(bundle: local!bundle, bundleKey: _),
  concat(
    "</w:t></w:r></w:p>",
    rule!AS_GSS_UT_createDynamicTableHeaderRow(
      headers: {
        local!dynamicLabel("lbl_Signatures") & ":",
        local!dynamicLabel("lbl_Username") & ":",
        local!dynamicLabel("lbl_DateTime") & ":",

      },
      /*Not adding this below hex code to Branding file,
        because we don't support customization in documents*/
      tableBorderColor: "ffffff",
      fontSize: 22
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!consensusSignatures,
      expression: rule!AS_GSS_UT_createTableRowWithDynamicColumns(
        cells: {
          concat(
            a!match(
              value: tostring(
                fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username]
              ),
              whenTrue: local!factorChair = fv!value,
              then: "Factor Chair",
              whenTrue: contains(local!evaluators, fv!value),
              then: "Evaluator",
              default: ""
            ),
            ": ",
            if(
              /*Mask Evaluators name if flag is set to true*/
              or(
                local!factorChair = tostring(
                  fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username]
                ),
                local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked] = false()
              ),
              rule!AS_GAM_UT_concatUserFirstNameLastName(
                firstName: rule!AS_CO_UT_indexWhere(
                  value: fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username],
                  initialArray: local!userDetails.username,
                  finalArray: local!userDetails.firstName
                ),
                lastName: rule!AS_CO_UT_indexWhere(
                  value: fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username],
                  initialArray: local!userDetails.username,
                  finalArray: local!userDetails.lastName
                )
              ),
              rule!AS_GSS_UT_returnNickName(
                userNames: fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username],
                nickNamesList: local!evaluatorNickNames
              )
            )
          ),
          if(
            /*When mask evaluators is true, usernames should not be shown for evaluators*/
            or(
              local!factorChair = tostring(
                fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username]
              ),
              local!evaluation[recordType!AS_GSS_Evaluation_SYNCEDRECORD.isEvaluatorMasked] = false()
            ),
            fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.username],
            cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT
          ),
          rule!AS_GSS_CO_UT_formatDateTimeTextWithTimezone(
            dateTime: fv!item[recordType!AS_GSS_ConsensusSignature_SYNCEDRECORD.signatureTimestamp],
            timezoneText: "EST",
            timezoneValue: "EST5EDT"
          )
        },
        /*Not adding this below hex code to Branding file,
        because we don't support customization in documents*/
        tableBorderColor: "ffffff",
        fontSize: 22
      )
    ),
    "<w:p><w:r><w:t>"
  )
)

