        local!array
      ),

      /*Have to handle separately for group types due to how it casts to string*/
      rule!AS_CO_UT_typeCompare(
        value1: local!array,
        value2: {togroup(null)}
      ),
      wherecontains(
        togroup(ri!values),
        local!array
      ),

      /*Casting to string accounts for any primitive type (integer, boolean, ect.)*/
      wherecontains(
        if(
          rule!AS_CO_UT_isBlank(ri!values),
          touniformstring(ri!values),
          touniformstring(a!flatten(ri!values))
        ),
        touniformstring(local!array)
      )
    )
  )
)

