/* to prevent modulus from breaking */
  isnull(ri!value),
  ri!value,
  if(
    todecimal(ri!value) = tostring(ri!value),
    if(
      mod(ri!value, 1) = 0,
      text(
        todecimal(ri!value), "##"
      ),
      text(
        todecimal(ri!value), "##.##"
      )
    ),
    ri!value
  )
)

