#"SYSTEM_SYSRULES_ButtonLayout"(
  primaryButtons: {
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Update",
      value: cons!AS_GSS_ENUM_USER_ACTION_UPDATE,
      style: "SOLID",
      validate: true(),
      submit: true(),
      saveInto: {
        ri!buttonAction,
        ri!additionalSaves,
        a!save(
          ri!evaluationVendor,
          rule!AS_GSS_UT_reconcileVendorDocsAndMappingsOnUpdate(
            originalVendorDocuments: ri!originalVendorDocuments,
            evaluationVendor: ri!currentEvaluationVendor
          )
        ),
        a!save(
          ri!documentInfo,
          a!map(
            documentsToDelete: rule!AS_CO_UT_rejectNullValuesFromArray(
              array: difference(
                tointeger(ri!uploadedTemporaryDocIds),
                tointeger(
                  ri!currentEvaluationVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents.appianDocId]
                )
              )
            ),
            documentsToMove: rule!AS_CO_UT_rejectNullValuesFromArray(
              array: tointeger(
                rule!AS_CO_UT_indexWhere(
                  value: cons!AS_GSS_REF_ID_SOURCE_APPLICATION_GSS,
                  initialArray: a!flatten(
                    ri!currentEvaluationVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents.sourceApplicationId]
                  ),
                  finalArray: a!flatten(
                    ri!currentEvaluationVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents.appianDocId]
                  ),
                  isRemove: false()
                )
              )
            )
          )
        )
      },
      disabled: or(
        a!defaultValue(
          value: ri!integrationFailed,
          default: false()
        ),
        not(
          rule!AS_GSS_UT_areVendorDetailsComplete(
            pendingUploadDocIds: ri!pendingUploadDocIds,
            evaluationVendorDetails: ri!currentEvaluationVendor,
            bundle: ri!bundle
          )
        )
      )
    )
  },
  secondaryButtons: {
    #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: "Cancel",
      value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
      style: "OUTLINE",
      validate: false(),
      submit: true(),
      saveInto: {
        ri!buttonAction,
        a!save(
          ri!documentInfo,
          a!map(
            documentsToDelete: rule!AS_CO_UT_rejectNullValuesFromArray(array: ri!uploadedTemporaryDocIds),
            documentsToMove: {}
          )
        )
      }
    )
  }
)

