/*If the audit data is not nested and is manipulated and passed from outsite, we can directly construct the audit entry for each ri!auditParams.manipulationAudit*/
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams.manipulationAudit,
  expression: rule!AS_CO_UT_dynamicDictionary(
    fieldValuePairs: {
      if(
        rule!AS_CO_UT_isBlank(
          index(
            ri!auditParams.auditConfig,
            "idField",
            null
          )
        ),
        {},
        {
          field: ri!auditParams.auditConfig.idField,
          value: index(
            index(
              ri!auditParams.newObjects,
              tostring(ri!auditParams.auditConfig.idField),
              null
            ),
            1,
            null
          )
        }
      ),
      { field: "username", value: ri!initiator },
      { field: "timestamp", value: now() },
      {
        field: "auditActionCode",
        value: /*If the action code varies for each manipulation audit, actionCode within auditParams.manipulationAudit will be used. Else, externalAction will be used*/
        rule!AS_CO_UT_replaceNull(
          nullableValue: fv!item.actionCode,
          replacementValue: ri!auditParams.externalAction
        )
      },
      if(
        rule!AS_CO_UT_isNotBlank(
          ri!auditParams.auditConfig.foreignIdField
        ),
        {
          field: ri!auditParams.auditConfig.foreignIdField,
          value: index(
            index(
              ri!auditParams.newObjects,
              tostring(
                ri!auditParams.auditConfig.foreignIdField
              ),
              null
            ),
            1,
            null
          )
        },
        {}
      ),
      /*The field changes will be passed as label value pairs in "fieldChanges" column of auditParams.manipulationAudit"*/
      {
        field: "simpleFieldChanges",
        value: rule!AS_CO_UT_castToDictionaryList(value: fv!item.fieldChanges)
      }
    }
  )
)

#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "newObjects",
      value: rule!AS_ADT_UT_getNewObjectsAfterWritingFromMutipleDSE(
        auditParams: if(
          rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
          a!flatten(fv!item),
          rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: "auditConfig",
            newValues: rule!AS_CO_UT_filterCdtByField(
              cdt: fv!item.auditConfig.complexFields,
              field: "auditFieldName",
              value: tostring(fv!item.complexFieldName)
            )
          )
        ),
        storedValues: {
          entity: fv!item.objectDse,
          data: rule!AS_GAM_UT_getExpectedOutputDataFromStoredValue(
            storedValues: ri!storedValues,
            entity: fv!item.objectDse
          )
        }
      )
    }
  )
)
/*Note: Could not add test case as it would require an entity constant*/

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
  a!localVariables(
    local!length: len(ri!value),
    local!minLength: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!minLength,
      replacementValue: 0
    ),
    if(
      /* Does not meet exact length requirement */
      and(
        rule!AS_CO_UT_isNotBlank(ri!minLength),
        rule!AS_CO_UT_isNotBlank(ri!maxLength),
        ri!minLength = ri!maxLength,
        local!length <> ri!minLength
      ),
      rule!AS_CO_UT_replaceNull(
        nullableValue: rule!AS_CO_UT_replaceNull(
          nullableValue: ri!maxLengthMessage,
          replacementValue: ri!minLengthMessage
        ),
        replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: if(
            ri!minLength = 1,
            "AS.CO.CommonObjects.vld_EnterExactlySingle",
            "AS.CO.CommonObjects.vld_EnterExactlyMultiple"
          ),
          arguments: ri!minLength
        )
      ),
      /*Too short*/
      if(
        local!length < ri!minLength,
        rule!AS_CO_UT_replaceNull(
          nullableValue: ri!minLengthMessage,
          replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
            bundleKey: "AS.CO.CommonObjects.vld_EnterAtLeast",
            arguments: local!minLength
          )
        ),
        /*Too long*/
        if(
          and(
            rule!AS_CO_UT_isNotBlank(ri!maxLength),
            local!length > ri!maxLength
          ),
          rule!AS_CO_UT_replaceNull(
            nullableValue: ri!maxLengthMessage,
            replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
              bundleKey: if(
                ri!maxLength = 1,
                "AS.CO.CommonObjects.vld_EnterNoMoreThanSingle",
                "AS.CO.CommonObjects.vld_EnterNoMoreThanMultiple"
              ),
              arguments: ri!maxLength
            )
          ),
          /*Valid*/
          ""
        )
      )
    )
  )
)

