a!localVariables(
  /*This will filter the documents that have been removed from the grid*/
  local!removedDocs: remove(
    ri!originalVendorDocuments,
    rule!AS_CO_UT_whereContains(
      values: ri!evaluationVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents.evaluationDocumentId],
      array: ri!originalVendorDocuments[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId]
    )
  ),
  /*This will update the isDeleted column of the removed documents and also this deactivates*/
  /*any factor document mapping available for the corresponding document*/
  local!deactivatedDocs: #"SYSTEM_SYSRULES_forEach"(
    items: local!removedDocs,
    expression: rule!AS_GSS_UT_updateRecordsByModelRecord(
      records: fv!item,
      modelRecord: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD(
        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.isDeleted: true(),
        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping: #"SYSTEM_SYSRULES_forEach"(
          items: fv!item[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping],
          expression: rule!AS_GSS_UT_updateRecordsByModelRecord(
            records: fv!item,
            modelRecord: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD(
              recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.isActive: false()
            )
          )
        )
      )
    )
  ),
  /*This will check if any factor document mappings of the final document list has been modified.*/
  /*If a factor document mapping has been removed, then that entry is deactivated and added to the list*/
  local!updatedDocs: #"SYSTEM_SYSRULES_forEach"(
    items: ri!evaluationVendor[recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents],
    expression: if(
      rule!AS_CO_UT_checkIsNullOrEmpty(
        value: fv!item[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId]
      ),
      fv!item,
      a!localVariables(
        /*The iteration will iterate for each document in the final list.*/
        /*.*/
        /*.*/
        /*This will hold all the original factor document mappings of the document*/
        local!allExistingMappings: a!flatten(
          index(
            rule!AS_CO_UT_indexWhere(
              value: fv!item[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId],
              initialArray: ri!originalVendorDocuments[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.evaluationDocumentId],
              finalArray: ri!originalVendorDocuments,
              isRemove: false()
            ),
            recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping,
            {}
          )
        ),
        /*This will filter the factor document mappings that have been removed*/
        local!removedMappings: remove(
          local!allExistingMappings,
          rule!AS_CO_UT_whereContains(
            values: fv!item[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping.documentMappingId],
            array: local!allExistingMappings[recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentMappingId]
          )
        ),
        /*This will deactivate the removed factor document mappings*/
        local!deactivatedMappings: rule!AS_GSS_UT_updateRecordsByModelRecord(
          records: local!removedMappings,
          modelRecord: recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD(
            recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.isActive: false()
          )
        ),
        if(
          rule!AS_CO_UT_checkIsNullOrEmpty(
            value: index(
              local!deactivatedMappings,
              recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.documentMappingId,
              null
            )
          ),
          fv!item,
          rule!AS_GSS_UT_updateRecordsByModelRecord(
            records: fv!item,
            modelRecord: recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD(
              recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping: append(
                fv!item[recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping],
                local!deactivatedMappings
              )
            )
          )
        )
      )
    )
  ),
  rule!AS_GSS_UT_updateRecordsByModelRecord(
    records: ri!evaluationVendor,
    modelRecord: recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD(
      recordType!AS_GSS_EvaluationVendor_SYNCEDRECORD.documents: append(
        local!updatedDocs,
        local!deactivatedDocs
      )
    )
  )
)

