                        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.fileType: rule!AS_GSS_UT_findDocumentNameAndExtensionById(fv!item).extension,
                        recordType!AS_GSS_EvaluationDocument_SYNCEDRECORD.vendorDocumentFactorMapping: if(
                          rule!AS_CO_UT_isBlank(ri!defaultFactorId),
                          null,
                          recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD(
                            recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.factorId: ri!defaultFactorId,
                            recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.createdBy: loggedInUser(),
                            recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.createdDatetime: now(),
                            recordType!AS_GSS_FactorDocumentMapping_SYNCEDRECORD.isActive: true()
                          )
                        )
                      )
                    )
                  )
                ),
                a!save(ri!isUploadInProgress, save!value),
                a!save(
                  ri!uploadedTemporaryDocIds,
                  append(
                    ri!uploadedTemporaryDocIds,
                    ri!uploadedDocIds
                  )
                ),
                a!save(ri!uploadedDocIds, null)
              },
              style: "SOLID",
              disabled: or(
                a!isNullOrEmpty(ri!uploadedDocIds),
                a!isNotNullOrEmpty(
                  difference(
                    #"SYSTEM_SYSRULES_forEach"(
                      items: ri!uploadedDocIds,
                      expression: rule!AS_GSS_UT_findDocumentNameAndExtensionById(fv!item).extension
                    ),
                    cons!AS_GSS_TXT_VALID_FILE_TYPES_FOR_EVALUATION_DOCUMENT
                  )
                )
              ),
              accessibilityText: if(
                or(
                  a!isNullOrEmpty(ri!uploadedDocIds),
                  a!isNotNullOrEmpty(
                    difference(
                      #"SYSTEM_SYSRULES_forEach"(
                        items: ri!uploadedDocIds,
                        expression: rule!AS_GSS_UT_findDocumentNameAndExtensionById(fv!item).extension
                      ),
                      cons!AS_GSS_TXT_VALID_FILE_TYPES_FOR_EVALUATION_DOCUMENT
                    )
                  )
                ),
                "Please upload the document to enable the upload button.",
                "button enabled"
              )
            ),
            marginBelow: "NONE"
          )
        )
      }
    )
  },
  showWhen: ri!isUploadInProgress,
  shape: "SEMI_ROUNDED",
  padding: "STANDARD",
  marginBelow: "STANDARD"
)

