_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "newObjects",
      value: rule!AS_ADT_UT_getNewObjectsAfterWritingFromMutipleDSE(
        auditParams: if(
          rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
          a!flatten(fv!item),
          rule!AS_CO_UT_updateCdtFields(
            cdt: fv!item,
            fields: "auditConfig",
            newValues: rule!AS_CO_UT_filterCdtByField(
              cdt: fv!item.auditConfig.complexFields,
              field: "auditFieldName",
              value: tostring(fv!item.complexFieldName)
            )
          )
        ),
        storedValues: {
          entity: fv!item.objectDse,
          data: rule!AS_GAM_UT_getExpectedOutputDataFromStoredValue(
            storedValues: ri!storedValues,
            entity: fv!item.objectDse
          )
        }
      )
    }
  )
)
/*Note: Could not add test case as it would require an entity constant*/

if(
  /*Action only audit*/
  ri!auditParams.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_ACTION_ONLY,
  rule!AS_GAM_ADT_UT_constructActionOnlyAuditData(
    initiator: ri!initiator,
    auditParams: ri!auditParams
  ),
  /*Manipulation Audit*/
  if(
    ri!auditParams.auditType = cons!AS_GAM_ADT_TXT_AUDIT_TYPE_MANIPULATION,
    rule!AS_GAM_ADT_UT_constructManipulatedAuditData(
      initiator: ri!initiator,
      auditParams: ri!auditParams
    ),
    /*Generic field level audit*/
    rule!AS_GAM_ADT_UT_buildFieldLevelAuditEntityData(auditParams: ri!auditParams)
  )
)

a!localVariables(
  /*Indexing 1 to convert list of dictionary to list*/
  local!complexFieldConfig: index(
    rule!AS_CO_UT_filterCdtByField(
      cdt: ri!auditParams.auditConfig.complexFields,
      field: "auditFieldName",
      value: tostring(ri!auditParams.complexFieldName)
    ),
    1,
    null
  ),
  local!foreignIdField: tostring(
    a!flatten(
      index(
        local!complexFieldConfig,
        "foreignIdField",
        null
      )
    )
  ),
  local!auditConfig: rule!AS_CO_UT_dynamicDictionary(
    fieldValuePairs: {
      /*Field value pairs for parent object audit*/
      { field: "username", value: ri!initiator },
      { field: "timestamp", value: now() },
      {
        field: "auditActionCode",
        value: ri!auditParams.externalAction
      },
      /*Indexing the "idField" of the parent object using "foreignIdField"*/
      if(
        rule!AS_CO_UT_isBlank(local!foreignIdField),
        {},
        {
          field: ri!auditParams.auditConfig.idField,
          value: tointeger(
            rule!AS_CO_UT_replaceNull(
              nullableValue: index(
                rule!AS_CO_UT_indexPath(
                  cdt: ri!auditParams.newObjects,
                  field: ri!auditParams.auditConfig.idField
                ),
                1,
                null
              ),
              replacementValue: index(
                rule!AS_CO_UT_indexPath(
                  cdt: ri!auditParams.newObjects,
                  field: local!foreignIdField
                ),
                1,
                null
              )
            )
          )
        }
      ),
      {
        field: tostring(ri!auditParams.complexFieldName),
        value: if(
          rule!AS_CO_UT_isBlank(ri!auditParams.manipulationAudit),
          /*If the audit calculation for the nested CDT involves generic field changes (i.e the manipulation audit is null), we can directly use the field change rule to calculate the audit*/
          rule!AS_ADT_UT_calculateAudit(
            originalObjects: rule!AS_CO_UT_castToDictionaryList(value: ri!auditParams.originalObjects),
            newObjects: rule!AS_CO_UT_castToDictionaryList(value: ri!auditParams.newObjects),
            objectTypeNumber: ri!auditParams.objectTypeNumber,
            auditConfig: rule!AS_CO_UT_castToDictionary(value: local!complexFieldConfig),
            namespace: ri!auditParams.namespace,
            username: ri!initiator,
            externalAction: null
          ),
          /*If the audit data is nested and is manipulated and passed from outsite, we can directly construct the audit entry for each ri!auditParams.manipulationAudit*/
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!auditParams.manipulationAudit,
            expression: rule!AS_CO_UT_dynamicDictionary(
              fieldValuePairs: {
                if(
                  rule!AS_CO_UT_isNotBlank(local!complexFieldConfig.idField),
                  {
                    field: local!complexFieldConfig.idField,
                    value: index(
                      ri!auditParams.newObjects,
                      tostring(local!complexFieldConfig.idField),
                      null
                    )
                  },
                  {}
                ),
                { field: "username", value: ri!initiator },
                { field: "timestamp", value: now() },
                {
                  field: "auditActionCode",
                  value: /*If the action code varies for each manipulation audit, actionCode within auditParams.manipulationAudit will be used. Else, externalAction will be used*/
                  rule!AS_CO_UT_replaceNull(
                    nullableValue: fv!item.actionCode,
                    replacementValue: ri!auditParams.externalAction
                  )
                },
                if(
                  rule!AS_CO_UT_isNotBlank(
                    local!complexFieldConfig.foreignIdField
                  ),
                  {
                    field: local!complexFieldConfig.foreignIdField,
                    value: index(
                      rule!AS_CO_UT_indexPath(
                        cdt: ri!auditParams.newObjects,
                        field: local!complexFieldConfig.foreignIdField
                      ),
                      1,
                      null
                    )
                  },
                  {}
                ),
                /*The field changes will be passed as label value pairs in "fieldChanges" column of auditParams.manipulationAudit"*/
                {
                  field: "simpleFieldChanges",
                  value: rule!AS_CO_UT_castToDictionaryList(value: fv!item.fieldChanges)
                }
              }
            )
          )
        )
      }
    }
  ),
  if(
    /*To make the action code "UNCHANGED" if the object is unmodified(excluding the metadata)*/
    rule!AS_CO_UT_not(
      contains(
        a!flatten(
          {
            rule!AS_CO_UT_isBlank(
              index(
                rule!AS_CO_UT_indexPath(
                  cdt: local!auditConfig,
                  field: tostring(ri!auditParams.complexFieldName)
                ),
                "simpleFieldChanges",
                null
              )
            ),
            #"SYSTEM_SYSRULES_forEach"(
              items: {
                index(
                  index(
                    local!complexFieldConfig,
                    "complexFields",
                    null
                  ),
                  "auditFieldName",
                  null
                )
              },
              expression: rule!AS_CO_UT_isBlank(
                rule!AS_CO_UT_rejectCdtByField(
                  cdt: index(
                    rule!AS_CO_UT_indexPath(
                      cdt: local!auditConfig,
                      field: tostring(ri!auditParams.complexFieldName)
                    ),
                    fv!item,
                    null
                  ),
                  field: "auditActionCode",
                  value: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
                )
              )
            )
          }
        ),
        false
      )
    ),
    rule!AS_CO_UT_updateCdtFields(
      cdt: local!auditConfig,
      fields: "auditActionCode",
      newValues: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
    ),
    local!auditConfig
  )
)

