rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS_GSS_Criteria_SYNCEDRECORD,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.criteriaId,
      operator: "in",
      value: ri!criteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluationId,
      operator: "in",
      value: ri!evaluationId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.parentCriteriaId,
      operator: a!match(
        value: ri!isParent,
        equals: true,
        then: "is null",
        equals: false,
        then: "not null",
        default: "in"
      ),
      value: ri!parentCriteriaId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS_GSS_Criteria_SYNCEDRECORD.isActive,
      operator: "in",
      value: a!defaultValue(
        value: ri!isActive,
        default: {true}
      )
    ),
    ri!additionalfilters
  },
  relatedRecordData: ri!relatedRecordData,
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS_GSS_Criteria_SYNCEDRECORD.CriteriaAssignment,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.EvaluatorTeam,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.Rating,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.evaluation,
          recordType!AS_GSS_Criteria_SYNCEDRECORD.SubCriteria
        },
        {}
      )
    }
  ),
  sort: ri!sortInfo,
  pagingInfo: ri!pagingInfo,
  returnType: ri!returnType,
  executeWhen: ri!executeWhen,
  triggerRefresh: ri!triggerRefresh,
  logicalExpression: ri!logicalExpression,
  aggregationFields: ri!aggregationFields
)

