      #"urn:appian:user-filter:v1:9a04b944-b726-41f5-9b37-8ec71b6cc370/cd37b4b3-9ee7-4f9a-a44a-14375ce5011e",
      if(
        not(ri!isEvaluator),
        #"urn:appian:user-filter:v1:9a04b944-b726-41f5-9b37-8ec71b6cc370/9b563bdd-0299-48a9-b050-cbf1b9f73a32",
