    a!localVariables(
      local!batchSize: 50,
      local!logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        ignoreFiltersWithEmptyValues: true,
        logicalExpressions: {
          a!defaultValue(ri!logicalExpression, {}),
