            operator: "AND",
            ignoreFiltersWithEmptyValues: true,
            filters: a!defaultValue(ri!filters, {})
          )
        }
      ),
      local!totalCount: a!refreshVariable(
        value: #"SYSTEM_SYSRULES_queryEntity_v2"(
          entity: ri!entity,
          fetchTotalCount: true,
          query: #"SYSTEM_SYSRULES_query"(
            logicalExpression: local!logicalExpression,
            pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 0)
          )
        ).totalCount,
        refreshOnVarChange: ri!triggerRefresh
      ),
      local!selection: if(
        ri!excludeNestedCdts = true,
        #"SYSTEM_SYSRULES_querySelection"(
          columns: #"SYSTEM_SYSRULES_forEach"(
            items: rule!AS_CO_UT_returnCdtFields(
              type: ri!entityType,
              onlyPrimitiveFields: true
            ),
            expression: #"SYSTEM_SYSRULES_queryColumn"(field: fv!item)
          )
        ),
        if(
          a!isNotNullOrEmpty(ri!querySelection),
          ri!querySelection,
          null
        )
      ),
      local!data: a!refreshVariable(
        value: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(
              ceiling(local!totalCount / local!batchSize)
            ) * local!batchSize + 1,
            expression: #"SYSTEM_SYSRULES_queryEntity_v2"(
              entity: ri!entity,
              query: #"SYSTEM_SYSRULES_query"(
                selection: local!selection,
