            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AS_GSS_UT_displayEvaluationRecordTitle(
                evaluationNumber: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationNumber],
                evaluationTitle: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluation.evaluationTitle]
              ),
              color: "ACCENT",
              link: #"SYSTEM_SYSRULES_recordLink"(
                recordType: recordType!AS_GSS_Evaluation_RECORD,
                identifier: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.evaluationId],

              ),
              linkStyle: "STANDALONE"
            ),

