        )
      ),
      #"SYSTEM_SYSRULES_gridColumn"(
        label: "Approach Name",
        sortField: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskChecklist.listName,
        value: #"SYSTEM_SYSRULES_richTextDisplayField"(
          accessibilityText: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskChecklist.listName],
          value: {
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: rule!AS_CO_UT_replaceNull(
                nullableValue: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskChecklist.listName],
                replacementValue: "-"
              )
            )
          }
        ),

      )
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Team",
      sortField: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.EvaluatorTeam.teamName,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        preventWrapping: true,
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_replaceNull(
              nullableValue: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.criteria.EvaluatorTeam.teamName],
              replacementValue: cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT
            )
          )
        }
      ),
      showWhen: and(ri!isEvaluator, ri!isHomepage)
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
