        preventWrapping: true,
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_CO_UT_displayUser(
              user: rule!AS_CO_UT_displayGroupOrUser(
                groupOrUser: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
              ),
              showHyphenIfValueNull: true
            )
          )
        }
      ),
      showWhen: or(not(ri!isEvaluator), not(ri!isHomePage))
    ),
    #"SYSTEM_SYSRULES_gridColumn"(
      label: "Assignee",
      sortField: recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee,
      value: #"SYSTEM_SYSRULES_richTextDisplayField"(
        preventWrapping: true,
        value: {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: if(
              rule!AS_CO_UT_isBlank(
                fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
              ),
              cons!AS_GSS_CO_NULL_REPLACEABLE_TEXT,
              rule!AS_CO_UT_userLinkDisplay(
                username: fv!row[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee]
              )
            )
          )
        }
      )
    ),
    rule!AS_GSS_TMG_UI_myTasksGrid_colDueDate(
      i18nData: ri!i18nData,
      loggedInUser: ri!user
    )
  },
  pageSize: cons!AS_CO_ENUM_PAGE_SIZE_MEDIUM,
