_a-0000ec6b-e6c2-8000-9d9d-011c48011c48_.sail_code:
or(
  /*Check if all tasks are blank*/
  rule!AS_CO_UT_isBlank(ri!selectedTasks),
  /*Need to Check*/
  rule!AS_CO_UT_isNotBlank(
    ri!selectedTasks[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.completionDate]
  ),
  rule!AS_CO_UT_isBlank(
    ri!selectedTasks[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.completionDate]
  ),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!selectedTasks,
    expression: if(
      /*First check that the task status is assigned*/
      rule!AS_CO_UT_not(
        contains(
          {
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED,
            cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS
          },
          fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskStatusId]
        )
      ),
      true,
      if(
        /*Then check if there is a default assignee on the task*/
        rule!AS_CO_UT_isNotBlank(
          fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee]
        ),
        /*Return false (not disabled) if EITHER the user is in the default assignee group OR the user is the individual assignee*/
        /*Returns true (disabled) when the task behaviour type is "Complete Evaluation" and the loggedInUser is not a userAssignee*/
        and(
          or(
            tostring(
              fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorSubtypeCode]
            ) = cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
            rule!AS_CO_UT_not(
              rule!AS_CO_UT_isUserMemberOfGroups(
                groups: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.groupAssignee],
                user: ri!user
              )
            )
          ),
          rule!AS_CO_UT_not(
            fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
          )
        ),
        /*Return false (not disabled) if the user is the individual assignee*/
        rule!AS_CO_UT_not(
          fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.userAssignee] = ri!user
        )
      )
    )
  ),
  /*check if any tasks not in checkbox*/
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!selectedTasks,
    expression: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode] <> cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_CHECKBOX
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "originalObjects",
      value: rule!AS_CO_UT_replaceNull(
        nullableValue: a!flatten(fv!item.originalObjects),
        replacementValue: if(
          /*Initializing null when the objectDse is null*/
          rule!AS_CO_UT_isBlank(fv!item.objectDse),
          rule!AS_CO_UT_initializeBlankLocalVariable(
            type: a!flatten(fv!item).objectTypeNumber
          ),
          if(
            rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
            if(
              /*Initializing null when the idField in the audit config is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  a!flatten(fv!item.auditConfig),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(auditParams: fv!item),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            ),
            if(
              /*Initializing null when idField of complexField's auditConfig is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(fv!item.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              /*Getting the original object for the complexFieldName object*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(
                  auditParams: rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "auditConfig",
                    newValues: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.auditConfig.complexFields,
                      field: "auditFieldName",
                      value: tostring(fv!item.complexFieldName)
                    )
                  )
                ),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            )
          )
        )
      )
    }
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
  a!localVariables(
    local!length: len(ri!value),
    local!minLength: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!minLength,
      replacementValue: 0
    ),
    if(
      /* Does not meet exact length requirement */
      and(
        rule!AS_CO_UT_isNotBlank(ri!minLength),
        rule!AS_CO_UT_isNotBlank(ri!maxLength),
        ri!minLength = ri!maxLength,
        local!length <> ri!minLength
      ),
      rule!AS_CO_UT_replaceNull(
        nullableValue: rule!AS_CO_UT_replaceNull(
          nullableValue: ri!maxLengthMessage,
          replacementValue: ri!minLengthMessage
        ),
        replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: if(
            ri!minLength = 1,
            "AS.CO.CommonObjects.vld_EnterExactlySingle",
            "AS.CO.CommonObjects.vld_EnterExactlyMultiple"
          ),
          arguments: ri!minLength
        )
      ),
      /*Too short*/
      if(
        local!length < ri!minLength,
        rule!AS_CO_UT_replaceNull(
          nullableValue: ri!minLengthMessage,
          replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
            bundleKey: "AS.CO.CommonObjects.vld_EnterAtLeast",
            arguments: local!minLength
          )
        ),
        /*Too long*/
        if(
          and(
            rule!AS_CO_UT_isNotBlank(ri!maxLength),
            local!length > ri!maxLength
          ),
          rule!AS_CO_UT_replaceNull(
            nullableValue: ri!maxLengthMessage,
            replacementValue: rule!AS_CO_UT_displayCommonObjectBundleLabel(
              bundleKey: if(
                ri!maxLength = 1,
                "AS.CO.CommonObjects.vld_EnterNoMoreThanSingle",
                "AS.CO.CommonObjects.vld_EnterNoMoreThanMultiple"
              ),
              arguments: ri!maxLength
            )
          ),
          /*Valid*/
          ""
        )
      )
    )
  )
)

_a-0000ed2d-b9af-8000-9de8-011c48011c48_.sail_code:
/*This rule will handle below conversions*/
/*1. When toGMT is false, converts the date in the text format dd/mm/yyyy HH:MM AM/PM to formatted Datetime ouput.*/
/*2. When toGMT is true, converts the provided datetime to GMT text format to store in audit tables*/

if(
  a!isNullOrEmpty(value: ri!value),
  {},
  if(
    ri!toGmt,
    datevalue(ri!value) & " " & timevalue(ri!value),
    rule!AS_CO_UT_formatDateTime(
      dateTime: todatetime(ri!value)
    )
  )
)

