  local!allAssignments: #"SYSTEM_SYSRULES_forEach"(
    items: rule!AS_CO_UT_distinct(ri!allTeamMembers),
    expression: {
      a!localVariables(
        local!currentAssignment: rule!AS_CO_UT_filterCdtByField(
          cdt: ri!criteria.assignments,
          field: "assignee.username",
          value: fv!item
        ),
        if(
          contains(ri!selectedAssignees, fv!item),
          /*TeamMember is selected in dropdown*/
          if(
            contains(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!criteria.assignments,
                field: "isActive",
                value: true
              ).assignee.username,
              fv!item
            ),
            /*Retaining existing active assignments and adding new assignments added*/
            local!currentAssignment,
            if(
              contains(
                rule!AS_CO_UT_filterCdtByField(
                  cdt: ri!criteria.assignments,
                  field: "isActive",
                  value: false
                ).assignee.username,
                fv!item
              ),
              /*Reactivating assignments*/
              rule!AS_CO_UT_updateCdtFields(
                cdt: local!currentAssignment,
                fields: "isActive",
                newValues: true
              ),
              /*New assignments*/
              rule!AS_CO_UT_cast(
                type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments',
                value: {
                  assignmentId: null,
                  assignee: rule!AS_CO_UT_filterCdtByField(
                    cdt: ri!userDetails,
                    field: "username",
                    value: fv!item
                  ),
                  isActive: true
                }
              )
            )
          ),
          /*Team Member is not selected in dropdown*/
          if(
            rule!AS_CO_UT_isBlank(local!currentAssignment.assignmentId),
            null,
            rule!AS_CO_UT_updateCdtFields(
              cdt: local!currentAssignment,
              fields: "isActive",
              newValues: false
            )
          )
        )
      )
    }
  ),
  rule!AS_CO_UT_cast(
    type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_Criteria_Assignments?list',
    value: rule!AS_CO_UT_rejectNullValuesFromArray({ local!allAssignments })
  )
)

