a!localVariables(
  local!userDetails: rule!AS_GSS_QE_getUser(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    executeWhen: true
  ),
  local!isExpand: a!refreshVariable(
    value: repeat(length({ ri!evaluationFactors }), true),
    refreshOnReferencedVarChange: false
  ),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!evaluationFactors,
    expression: /*Factors*/
    a!localVariables(
      local!subFactors: a!refreshVariable(
        value: rule!AS_CO_UT_filterCdtByField(
          cdt: rule!AS_CO_UT_filterCdtByField(
            cdt: ri!evaluationSubFactors,
            field: "isActive",
            value: true
          ),
          field: "parentCriteriaId",
          value: fv!item.criteriaId
        ),
        refreshOnVarChange: {
          ri!evaluationFactors,
          ri!evaluationSubFactors
        }
      ),
      local!hasSubFactor: rule!AS_CO_UT_isNotBlank(local!subFactors),
      local!applyDueDateVisibility: a!refreshVariable(
        value: count(
          rule!AS_CO_UT_distinct(
            {
              local!subFactors.dueDate,
              fv!item.dueDate
            }
          )
        ) = 1,
        refreshOnVarChange: {
          ri!evaluationFactors,
          ri!evaluationSubFactors
        }
      ),
      local!applyEvaluatorsVisibility: a!refreshVariable(
        value: and(
          a!localVariables(
            local!currentFactorAssignment: ri!evaluationFactors[fv!index].assignments,
            local!factorUserName: rule!AS_CO_UT_filterCdtByField(
              cdt: local!currentFactorAssignment,
              field: "isActive",
              value: true
            ).assignee.username,
            #"SYSTEM_SYSRULES_forEach"(
              items: local!subFactors,
              expression: a!localVariables(
                local!subFactorAssignments: fv!item.assignments,
                local!subfactorUsername: rule!AS_CO_UT_filterCdtByField(
                  cdt: local!subFactorAssignments,
                  field: "isActive",
                  value: true
                ).assignee.username,
                {
                  rule!AS_CO_UT_isBlank(
                    symmetricdifference(
                      local!factorUserName,
                      local!subfactorUsername
                    )
                  )
                }
              )
            )
          )
        ),
        refreshOnVarChange: {
          ri!evaluationFactors,
          ri!evaluationSubFactors
        }
      ),
      {
        rule!AS_GSS_CPS_factorDetailsInAssignMembers(
          currentEvaluationFactor: fv!item,
          evaluationFactors: ri!evaluationFactors,
          index: fv!index,
          hasSubFactor: local!hasSubFactor,
          bundle: ri!bundle,
          evaluatorTeams: ri!evaluatorTeams,
          isExpand: local!isExpand,
          applyDueDateVisibility: local!applyDueDateVisibility,
          evaluationSubFactors: ri!evaluationSubFactors,
          applyEvaluatorsVisibility: local!applyEvaluatorsVisibility,
          userDetails: local!userDetails
        ),
        /*Associated Sub factors*/
        rule!AS_GSS_CPS_subfactorDetailsInAssignMembers(
          hasSubFactor: local!hasSubFactor,
          parentCriteriaId: fv!item.criteriaId,
          evaluationSubFactors: ri!evaluationSubFactors,
          isExpand: local!isExpand[fv!index],
          parentFactorName: fv!item.criteriaName,
          bundle: ri!bundle,
          evaluatorTeams: ri!evaluatorTeams,
          parentFactorDue: fv!item.dueDate,
          userDetails: local!userDetails
        )
      }
    )
  )
)

