rule!AS_CO_UT_queryEntity(
  entity: cons!AS_GSS_TMG_ENT_R_TASK_REF,
  entityType: 'type!{urn:com:appian:types:AS:GSS:TMG}AS_GSS_TMG_R_TaskRef',
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
    operator: "AND",
    logicalExpressions: {
      #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskRefId",
            operator: "=",
            value: ri!taskRefId
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskName",
            operator: "in",
            value: rule!AS_CO_UT_replaceNull(
              nullableValue: touniformstring(
                ri!taskName
              ),
              replacementValue: {}
            )
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskBehaviorType.configurationLevelCode",
            operator: "in",
            value: rule!AS_GSS_TMG_UT_returnConfigurationLevelCodeSet(
              configurationLevelCode: ri!configurationLevelCode
            )
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskCategory.taskCategoryId",
            operator: "in",
            value: ri!taskCategoryIds
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskRefId",
            operator: "not in",
            value: ri!taskRefIdsToHide
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskBehaviorType.behaviorTypeCode",
            operator: "=",
            value: ri!behaviorTypeCode
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskBehaviorType.behaviorSubtypeCode",
            operator: "=",
            value: ri!behaviorSubtypeCode
          ),

        },
        ignoreFiltersWithEmptyValues: true
      ),
      rule!AS_CO_UT_createSearchLogicalExpression(
        searchTerm: ri!searchText,
        fields: {
          "taskName",
          "taskCategory.categoryName",
          "modifiedBy"
        }
      )
    }
  ),
  pagingInfo: ri!pagingInfo
)

