        /*tempo safe link*/
        local!tempoUrl,
        if(
          isnull(ri!recordId),
          /* Link to the record list view. Navigates user to tempo */
          local!tempoUrl,
          /* Link to the record dashboard */
          substitute(
            local!tempoUrl,
            "/tempo/records/item",
            concat(
              "/sites/",
              ri!siteWebAddressIdentifier,
              if(
                isnull(ri!sitePage),
                "",
                concat("/page/", ri!sitePage)
              ),
              "/record"
            )
          )
        )
      )
    )
  )
)

