{
  if(
    ri!isAdd,
    a!localVariables(
      local!teamMembership: #"SYSTEM_SYSRULES_forEach"(
        items: ri!newlyAddedUserNames,
        expression: if(
          contains(
            ri!selectedEvaluatorTeam.teamMembership.member.username,
            fv!item
          ),
          rule!AS_CO_UT_updateCdtFields(
            cdt: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!selectedEvaluatorTeam.teamMembership,
              field: "member.username",
              value: fv!item
            )[1],
            fields: "isActive",
            newValues: true
          ),
          rule!AS_CO_UT_cast(
            type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_TeamMembership',
            value: {
              teamMembershipId: null,
              member: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!allGSSUsers,
                field: "username",
                value: fv!item
              ),
              isActive: true,
              createdBy: null,
              createdDatetime: null
            }
          )
        )
      ),
      {
        a!save(
          ri!selectedEvaluatorTeam.teamMembership,
          append(
            rule!AS_CO_UT_indexWhere(
              isRemove: true,
              value: ri!newlyAddedUserNames,
              initialArray: ri!selectedEvaluatorTeam.teamMembership.member.username,
              finalArray: ri!selectedEvaluatorTeam.teamMembership
            ),
            local!teamMembership
          )
        ),
        /*Generate alias names for new evaluators and update existing alias list*/
        a!save(
          ri!evaluatorAliases,
          append(
            ri!evaluatorAliases,
            rule!AS_GSS_generateAliasName(
              username: difference(
                ri!newlyAddedUserNames,
                touniformstring(
                  index(ri!evaluatorAliases, "userName", {})
                )
              ),
              evaluationId: ri!evaluationId,
              existingUserNickName: ri!evaluatorAliases,
              bundle: ri!bundle
            )
          )
        )
      }
    ),
    {
      a!localVariables(
        local!teamMembership: #"SYSTEM_SYSRULES_forEach"(
          items: ri!yetToBeRemovedMembers,
          expression: if(
            rule!AS_CO_UT_isBlank(fv!item.teamMembershipId),
            fv!item,
            rule!AS_CO_UT_updateCdtFields(
              cdt: rule!AS_CO_UT_filterCdtByField(
                cdt: ri!selectedEvaluatorTeam.teamMembership,
                field: "member.username",
                value: fv!item.member.username
              ),
              fields: "isActive",
              newValues: false
            )
          )
        ),
        a!save(
          ri!selectedEvaluatorTeam.teamMembership,
          append(
            rule!AS_CO_UT_indexWhere(
              isRemove: true,
              value: ri!yetToBeRemovedMembers.member.username,
              initialArray: ri!selectedEvaluatorTeam.teamMembership.member.username,
              finalArray: ri!selectedEvaluatorTeam.teamMembership
            ),
            remove(
              local!teamMembership,
              rule!AS_CO_UT_whereContains(
                values: null,
                array: local!teamMembership.teamMembershipId
              )
            )
          ),

        )
      )
    }
  ),
  /*Saving the selectedEvaluatorTeam details back to evaluatorTeam*/
  a!save(
    ri!evaluatorTeams[ri!activeTeamIndex].teamMembership,
    ri!selectedEvaluatorTeam.teamMembership
  )
}

