                originalCdt: if(
                  rule!AS_CO_UT_isBlank(fv!item.createdBy),
                  null,
                  rule!AS_CO_UT_indexPath(
                    cdt: a!flatten(
                      rule!AS_CO_UT_filterCdtByField(
                        cdt: ri!originalEvalFactors,
                        field: "criteriaId",
                        value: fv!item.criteriaId
                      )
                    ),
                    field: "assignments"
                  )
                ),
                updatedCdt: fv!item.assignments,
                modifiedBy: ri!initiator
              )
            }
          ),
          {
            field: "factorWeight",
            value: if(
              ri!evaluation.isWeightedFactorsRequired,
