  )
)

#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams,
  expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: fv!item,
    fieldValuePairs: {
      field: "originalObjects",
      value: rule!AS_CO_UT_replaceNull(
        nullableValue: a!flatten(fv!item.originalObjects),
        replacementValue: if(
          /*Initializing null when the objectDse is null*/
          rule!AS_CO_UT_isBlank(fv!item.objectDse),
          rule!AS_CO_UT_initializeBlankLocalVariable(
            type: a!flatten(fv!item).objectTypeNumber
          ),
          if(
            rule!AS_CO_UT_isBlank(fv!item.complexFieldName),
            if(
              /*Initializing null when the idField in the audit config is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  a!flatten(fv!item.auditConfig),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(auditParams: fv!item),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            ),
            if(
              /*Initializing null when idField of complexField's auditConfig is null*/
              rule!AS_CO_UT_isBlank(
                index(
                  rule!AS_CO_UT_filterCdtByField(
                    cdt: fv!item.auditConfig.complexFields,
                    field: "auditFieldName",
                    value: tostring(fv!item.complexFieldName)
                  ),
                  "idField",
                  null
                )
              ),
              rule!AS_CO_UT_initializeBlankLocalVariable(
                type: a!flatten(fv!item).objectTypeNumber
              ),
              /*Getting the original object for the complexFieldName object*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: rule!AS_ADT_UT_getOriginalObjects(
                  auditParams: rule!AS_CO_UT_updateCdtFields(
                    cdt: fv!item,
                    fields: "auditConfig",
                    newValues: rule!AS_CO_UT_filterCdtByField(
                      cdt: fv!item.auditConfig.complexFields,
                      field: "auditFieldName",
                      value: tostring(fv!item.complexFieldName)
                    )
                  )
                ),
                replacementValue: rule!AS_CO_UT_initializeBlankLocalVariable(
                  type: a!flatten(fv!item).objectTypeNumber
                )
              )
            )
          )
        )
      )
    }
  )
)

_a-0000e527-3b38-8000-9ba9-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_isBlank(ri!templateTask.duration),
  null,
  if(
    cons!AS_GSS_TMG_BOL_BUSINESS_DAYS_ONLY,
    /*Business days*/
    /*Duration Unit is Hours*/
    if(
      ri!templateTask.durationUnit.refLabel = cons!AS_GSS_REF_LABEL_ITEM_DURATION_UNITS[1],
      todate(
        caladdhours(
          if(
            ri!templateTask.noOfDaysFromStart <= 261,
            if(
              rule!AS_CO_UT_isBlank(ri!templateTask.noOfDaysFromStart),
              now(),
              caladddays(
                now(),
                ri!templateTask.noOfDaysFromStart
              )
            ),
            todate(
              reduce(
                fn!caladddays,
                merge({ today() }, { 0 }),
                {
                  repeat(
                    quotient(ri!templateTask.noOfDaysFromStart, 261),
                    261
                  ),
                  mod(ri!templateTask.noOfDaysFromStart, 261)
                }
              )
            )
          ),
          ri!templateTask.duration
        )
      ),
      /*Duration Unit is Days*/
      if(
        ri!templateTask.durationUnit.refLabel = cons!AS_GSS_REF_LABEL_ITEM_DURATION_UNITS[2],
        /*Logics to handle if the duration is more than 261 days*/
        if(
          ri!templateTask.duration + ri!templateTask.noOfDaysFromStart <= 261,
          todate(
            caladddays(
              now(),
              if(
                rule!AS_CO_UT_isBlank(ri!templateTask.noOfDaysFromStart),
                ri!templateTask.duration,
                ri!templateTask.duration + ri!templateTask.noOfDaysFromStart
              )
            )
          ),
          todate(
            reduce(
              fn!caladddays,
              merge({ today() }, { 0 }),
              {
                repeat(
                  quotient(
                    ri!templateTask.duration + ri!templateTask.noOfDaysFromStart,
                    261
                  ),
                  261
                ),
                mod(
                  ri!templateTask.duration + ri!templateTask.noOfDaysFromStart,
                  261
                )
              }
            )
          )
        ),
        /*Duration Unit is Weeks*/
        if(
          product(7, ri!templateTask.duration) <= 261,
          todate(
            caladddays(
              now(),
              if(
                rule!AS_CO_UT_isBlank(ri!templateTask.noOfDaysFromStart),
                product(7, ri!templateTask.duration),
                product(7, ri!templateTask.duration) + ri!templateTask.noOfDaysFromStart
              )
            )
          ),
          todate(
            reduce(
              fn!caladddays,
              merge({ today() }, { 0 }),
              {
                repeat(
                  quotient(
                    product(7, ri!templateTask.duration) + ri!templateTask.noOfDaysFromStart,
                    261
                  ),
                  261
                ),
                mod(
                  product(7, ri!templateTask.duration) + ri!templateTask.noOfDaysFromStart,
                  261
                )
              }
            )
          )
        )
      )
    ),
    /*Not Business days*/
    /*Duration Unit is Hours*/
    if(
      ri!templateTask.durationUnit.refLabel = cons!AS_GSS_REF_LABEL_ITEM_DURATION_UNITS[1],
      todate(
        now() + ri!templateTask.noOfDaysFromStart + intervalds(ri!templateTask.duration, 0, 0)
      ),
      /*Duration Unit is days*/
      if(
        ri!templateTask.durationUnit.refLabel = cons!AS_GSS_REF_LABEL_ITEM_DURATION_UNITS[2],
        todate(
          now() + ri!templateTask.noOfDaysFromStart + ri!templateTask.duration
        ),
        /*Duration Unit is Weeks*/
        todate(
          now() + ri!templateTask.noOfDaysFromStart + product(7, ri!templateTask.duration)
        )
      )
    )
  )
)

