a!localVariables(
  local!allFactors: a!flatten(
    {
      ri!evaluationFactors,
      ri!evaluationSubFactors
    }
  ),
  /*Update the tasks due date if the factor/subfactor due date was updated*/
  rule!AS_CO_UT_rejectNullValuesFromArray(
    #"SYSTEM_SYSRULES_forEach"(
      items: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!originalTasks,
        field: "taskStatus.taskStatusId",
        value: {
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_IN_PROGRESS,
          cons!AS_GSS_TMG_REF_ID_TASK_STATUS_ASSIGNED
        }
      ),
      expression: a!localVariables(
        local!criteriaDueDate: rule!AS_CO_UT_indexWhere(
          value: fv!item.criteria.criteriaId,
          initialArray: local!allFactors.criteriaId,
          finalArray: local!allFactors.dueDate
        )[1],
        local!currentUpdatedTask: if(
          and(
            rule!AS_CO_UT_isNotBlank(
              rule!AS_CO_UT_indexPath(
                cdt: ri!removedEvaluatorTasks,
                field: "taskId"
              )
            ),
            contains(
              tointeger(ri!removedEvaluatorTasks.taskId),
              tointeger(fv!item.taskId)
            )
          ),
          rule!AS_CO_UT_indexWhere(
            value: fv!item.taskId,
            initialArray: ri!removedEvaluatorTasks.taskId,
            finalArray: ri!removedEvaluatorTasks
          )[1],
          fv!item
        ),
        if(
          todate(fv!item.dueDate) <> local!criteriaDueDate,
          rule!AS_CO_UT_updateCdtFields(
            cdt: local!currentUpdatedTask,
            fields: "dueDate",
            newValues: local!criteriaDueDate
          ),
          null
        )
      )
    )
  )
)

