                          /*Returns true (disabled) when the task behaviour type is "Complete Evaluation" or "Complete LPTA Evaluation" and the loggedInUser is not a userAssignee*/
                          and(
                            or(
                              contains(
                                {
                                  cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_EVALUATION,
                                  cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_SUBTYPE_CODE_COMPLETE_LPTA_EVALUATION
