    fields: rule!AS_CO_UT_rejectNullValuesFromArray(
      array: {
        "isActive",
        ri!additionalFilterField
      }
    ),
    values: rule!AS_CO_UT_rejectNullValuesFromArray(
      array: {
        true,
        ri!additionalFilterValue
      }
    )
  ),
  local!selectedData: rule!AS_CO_UT_filterCdtByMultipleFields(
    cdt: ri!data,
    fields: if(
      rule!AS_CO_UT_isBlank(
        ri!additionalFilterField
      ),
      {
        ri!valueField
      },
      {
        ri!valueField,
        ri!additionalFilterField
      }
    ),
    values: if(
      rule!AS_CO_UT_isBlank(
        ri!additionalFilterField
      ),
      {
        ri!currentValue
      },
      {
        ri!currentValue,
        ri!additionalFilterValue
      }
    )
  ),
  /*Does not refresh to ensure that previously-selected values do not disappear while on a form*/
  local!initiallySelectedValue: a!refreshVariable(
    value: local!selectedData,
    refreshOnReferencedVarChange: false
  ),
  local!selectableReferenceData: rule!AS_CO_UT_cdtDistinctOnField(
    cdt: {
      local!filteredRefData,
      /* In case the options are filtered while something is selected */
      local!selectedData,
      /* So that the user can always return to the initial value */
      local!initiallySelectedValue
    },
    field: ri!valueField
  ),
  local!selectableReferenceDataLabelUpdate: #"SYSTEM_SYSRULES_forEach"(
    items: local!selectableReferenceData,
    expression: rule!AS_CO_UT_updateCdtFields(
      cdt: fv!item,
