if(
  or(
    a!isNullOrEmpty(value: ri!evaluationInfo.evaluators),
    a!isNullOrEmpty(
      value: ri!evaluationInfo.evaluatorsCount
    )
  ),
  {
    "-"
  },
  #"SYSTEM_SYSRULES_forEach"(
    items: enumerate(
      min(
        cons!AS_GSS_INT_MAX_COUNT_FOR_EVALUATORS_IN_EVALUATION_CARD,
        length(ri!evaluationInfo.evaluators)
      )
    ) + 1,
    expression: if(
      and(
        fv!isLast,
        fv!index = cons!AS_GSS_INT_MAX_COUNT_FOR_EVALUATORS_IN_EVALUATION_CARD
      ),
      a!map(
        initials: concat(
          "+",
          ri!evaluationInfo.evaluatorsCount - (fv!index - 1)
        ),
        fullName: joinarray(
          #"SYSTEM_SYSRULES_forEach"(
            items: ldrop(
              ri!evaluationInfo.evaluators,
              cons!AS_GSS_INT_MAX_COUNT_FOR_EVALUATORS_IN_EVALUATION_CARD-1
            ),
            expression: rule!AS_GSS_getUserFullName(
              username: fv!item
            )
          ),
          ", "
        )
      ),
      a!map(
        initials: rule!AS_GSS_CO_displayUserInitials(
          username: ri!evaluationInfo.evaluators[fv!index]
        ),
        fullName: rule!AS_GSS_getUserFullName(
          username: ri!evaluationInfo.evaluators[fv!index]
        )
      )
    )
  )
)

