      #"SYSTEM_SYSRULES_forEach"(
        items: local!tasks,
        expression: "<li>" & index(fv!item, "taskName", null) & if(
          or(
            rule!AS_CO_UT_isBlank(index(fv!item, "dueDate", null)),
            ri!hideDueDate
          ),
          "",
          {
            ": " & rule!AS_GAM_CO_I18N_UT_displayLabel(
              bundle: local!bundle,
              bundleKey: "eml_DueOn",
              /*Please make sure that arguments order is maintained as,*/
              /*[%1] is for <itemName>*/
              /*[%2] is for <evaluation>*/
              /*[%3] is for <dueDate>*/
              /*[%4] is for <oldDueDate>*/
              /*[%5] is for <changeReason>*/
              arguments: {
                null,
                null,
                rule!AS_CO_UT_formatDate(date: index(fv!item, "dueDate", null))
              }
            )
          }
        ) & "</li>"
      )
    },
    ""
  )
)

