              )
            ),
            showWhen: ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
          ),
          linkStyle: "STANDALONE",
          /*Only add caption when enabled*/
          caption: if(
            ri!pagingInfo.startIndex + ri!pagingInfo.batchSize > ri!totalCount,
            {},
            "Next"
          )
        )
      )
    ),
    #"SYSTEM_SYSRULES_sideBySideItem"(
      width: "MINIMIZE",
      item: #"SYSTEM_SYSRULES_richTextDisplayField"(
        labelPosition: "COLLAPSED",
        value: {
          " ",
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "angle-double-right-bold",
            size: "MEDIUM_PLUS",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              saveInto: a!save(
                ri!pagingInfo,
                #"SYSTEM_SYSRULES_pagingInfo"(
                  startIndex: if(
                    mod(ri!totalCount, ri!countPerPage) = 0,
                    ri!totalCount - (ri!countPerPage - 1),
                    ri!totalCount - mod(ri!totalCount, ri!countPerPage) + 1
                  ),
                  batchSize: ri!pagingInfo.batchSize,
                  sort: ri!pagingInfo.sort
                )
              ),
              showWhen: and(
                (
                  ri!totalCount - mod(ri!totalCount, ri!countPerPage)
                ) <= ri!totalCount,
                ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
              )
            ),
            linkStyle: "STANDALONE",
            /*Only add caption when enabled*/
            caption: if(
              and(
                (
                  ri!totalCount - mod(ri!totalCount, ri!countPerPage)
                ) <= ri!totalCount,
                ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
              ),
              "Last",
              {}
            ),
            color: if(
              and(
                (
                  ri!totalCount - mod(ri!totalCount, ri!countPerPage)
                ) <= ri!totalCount,
                ri!pagingInfo.startIndex + ri!pagingInfo.batchSize <= ri!totalCount
              ),
              "ACCENT",
              rule!AS_GSS_BrandingValueByKey(
                brandingKey: "GrayTwo",
                useSuiteBranding: true
              )
            )
          )
        }
      )
    )
  },
  marginBelow: "STANDARD",
  showWhen: ri!totalCount > ri!countPerPage
)

