_a-0000ed08-d796-8000-9dda-011c48011c48_.sail_code:
if(
  or(
    rule!AS_CO_UT_isNotBlank(ri!teamMembers),
    rule!AS_CO_UT_isNotBlank(ri!username)
  ),
  a!localVariables(
    local!loggedInUser:loggedInUser(),
    local!teamMembers: if(
      rule!AS_CO_UT_isNotBlank(ri!teamMembers),
      a!flatten(
        ri!teamMembers[recordType!AS_GSS_EvaluatorTeamMembership_SYNCEDRECORD.member]
      ),
      ri!username
    ),
    local!uniqueTeamMembers: union(local!teamMembers, local!teamMembers),
    /*Generate 5 digit random numbers based on constant configuration*/
    local!randomNumbers: tointeger(
      rand(
        cons!AS_GSS_INT_RANDOM_NUMBER_COUNT_FOR_MASK_EVALUATORS
      ) * 10000
    ) + 10000,
    /*Remove duplicates if any*/
    local!uniqueRandomNumbers: union(local!randomNumbers, tointeger({})),
    /*Remove already used random numbers for aliasNames within an evaluation*/
    local!unusedRandomNumbers: difference(
      local!uniqueRandomNumbers,
      tointeger(
        index(ri!existingUserNickName, "aliasName", {})
      )
    ),
    local!namesWithIds: #"SYSTEM_SYSRULES_forEach"(
      items: local!uniqueTeamMembers,
      expression: a!map(
        aliasId: null,
        evaluationId: ri!evaluationId,
        userName: fv!item,
        aliasName: if(
          contains(
            touniformstring(
              index(ri!existingUserNickName, "userName", {})
            ),
            fv!item
          ),
          tostring(
            index(
              index(
                ri!existingUserNickName,
                wherecontains(
                  fv!item,
                  touniformstring(
                    index(ri!existingUserNickName, "userName", {})
                  )
                ),
                null
              ),
              "aliasName",
              null
            )
          ),
          concat(
            "Evaluator",
            " ",
            local!unusedRandomNumbers[fv!index]
          )
        ),
        createdBy: local!loggedInUser,
        createdDatetime: now(),
        modifiedBy: local!loggedInUser,
        modifiedDatetime: now(),
        isActive: true()
      )
    ),
    local!namesWithIds
  ),
  {}
)

_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
a!localVariables(
  /*Get the distinct IDs*/
  local!distinctIds: rule!AS_CO_UT_distinct(
    tointeger(
      {
        index(
          ri!originalObjects,
          ri!auditConfig.idField,
          {}
        ),
        index(
          ri!newObjects,
          ri!auditConfig.idField,
          {}
        )
      }
    )
  ),
  if(
    rule!AS_CO_UT_isBlank(local!distinctIds),
    {},
    /*This flatten converts to a list of dictionary, which prevents odd behavior in the parent*/
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!distinctIds,
        expression: a!flatten(
          rule!AS_ADT_UT_calculateAudit_SingleObject(
            originalObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!originalObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            newObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!newObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            auditConfig: ri!auditConfig,
            username: ri!username,
            externalAction: ri!externalAction,
            auditType: ri!auditType
          )
        )
      )
    )
  )
)

