    items: ri!selectedTasks,
    expression: fv!item[recordType!AS_GSS_TMG_Task_SYNCEDRECORD.taskBehaviorType.behaviorTypeCode] <> cons!AS_GSS_TMG_ENUM_TASK_BEHAVIOR_TYPE_CODE_CHECKBOX
  )
