              label: ri!evaluatorTeams[local!activeTeamIndex].teamName,
              labelHeadingTag: "H2",
              labelColor: "STANDARD",
              contents: {
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
