              selectedEvaluatorTeam: local!selectedEvaluatorTeam,
              evaluatorTeams: ri!evaluatorTeams,
              activeTeamIndex: local!activeTeamIndex,
              showValidation: ri!showValidation,
              selection: local!selection,
