_a-0000e994-0db4-8000-9c52-011c48011c48_.sail_code:
a!localVariables(
  local!selectedStep: 1,
  local!bundle: rule!AS_GSS_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!showValidation: false,
  local!evaluatorAliases: rule!AS_GSS_UT_constructAliasMapsFromRecordInputs(
    evaluatorAliases: ri!maskEvaluatorDetails
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: "Update Evaluators",
    instructions: if(
      local!selectedStep = 1,
      rule!AS_GSS_UT_displayDynamicLabel(
        bundleKey: "txt_EditTeamStep",
        arguments: tostring(local!selectedStep)
      ),
      if(
        local!selectedStep = 2,
        rule!AS_GSS_UT_displayDynamicLabel(
          bundleKey: "txt_AssignMembersStep",
          arguments: tostring(local!selectedStep)
        ),
        {}
      )
    ),
    contents: {
      choose(
        local!selectedStep,
        {
          /*Edit Team Step */
          rule!AS_GSS_CPS_editTeamInUpdateEvaluators(
            i18nData: local!bundle,
            evaluatorTeams: ri!evaluatorTeams,
            showValidation: local!showValidation,
            evaluatorAliases: local!evaluatorAliases,
            evaluation: ri!evaluation
          )
        },
        {
          /* Assign Members Step */
          rule!AS_GSS_CPS_assignMembersInUpdateEvaluators(
            evaluationFactors: ri!evaluationFactors,
            evaluationSubFactors: ri!evaluationSubFactors,
            bundle: local!bundle,
            evaluatorTeams: ri!evaluatorTeams
          )
        }
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Update",
          submit: true,
          value: cons!AS_CO_ENUM_USER_ACTION_SUBMIT,
          saveInto: {
            ri!buttonAction,
            a!save(
              ri!maskEvaluatorDetails,
              #"SYSTEM_SYSRULES_forEach"(
                items: local!evaluatorAliases,
                expression: recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD(
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.aliasId: fv!item.aliasId,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.evaluationId: fv!item.evaluationId,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.aliasName: fv!item.aliasName,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.userName: fv!item.userName,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.createdBy: fv!item.createdBy,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.createdDatetime: fv!item.createdDatetime,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.modifiedBy: fv!item.modifiedBy,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.modifiedDatetime: fv!item.modifiedDatetime,
                  recordType!AS_GSS_MaskEvaluator_SYNCEDRECORD.isActive: fv!item.isActive
                )
              )
            )
          },
          style: "PRIMARY",
          validate: true,
          loadingIndicator: true,
          showWhen: local!selectedStep <> 1
        ),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Next",
          style: "PRIMARY",
          value: local!selectedStep + 1,
          showWhen: local!selectedStep <> 2,
          saveInto: {
            local!selectedStep,
            a!save(local!showValidation, false),
            a!save(
              ri!evaluationFactors,
              rule!AS_GSS_BL_updateTeamMembersAndAssignmentsForFactor(
                evalationFactor: ri!evaluationFactors,
                evaluatorTeams: ri!evaluatorTeams
              )
            ),
            a!save(
              ri!evaluationSubFactors,
              rule!AS_GSS_BL_updateTeamMembersAndAssignmentsForFactor(
                evalationFactor: ri!evaluationSubFactors,
                evaluatorTeams: ri!evaluatorTeams
              )
            )
          }
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Back",
          value: local!selectedStep - 1,
          saveInto: local!selectedStep,
          size: "STANDARD",
          style: "NORMAL",
          showWhen: local!selectedStep <> 1,
          validate: false
        ),
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Cancel",
          value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
          saveInto: { ri!buttonAction },
          size: "STANDARD",
          style: "LINK",
          submit: true,
          validate: false
        )
      }
    )
  )
)

#"SYSTEM_SYSRULES_cardLayout"(
  contents: {
    #"SYSTEM_SYSRULES_gridLayout"(
      label: "Subfactors",
      headerCells: {
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "ID"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Subfactor"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Team"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Evaluators" & " *"
        ),
        #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
          label: "Due Date"
        )
      },
      columnConfigs: {
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "ICON"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW"),
        #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "NARROW")
      },
      rows: #"SYSTEM_SYSRULES_forEach"(
        items: ri!evaluationSubFactors,
        expression: #"SYSTEM_SYSRULES_gridRowLayout"(
          contents: {
            rule!AS_CO_INP_textField(
              value: fv!item.factorNumber,
              readOnly: true
            ),
            rule!AS_CO_INP_textField(
              value: fv!item.criteriaName,
              readOnly: true
            ),
            /*Evaluator Team*/
            rule!AS_GSS_PCK_evaluationTeamInUpdateEvaluators(
              currentEvaluationFactor: fv!item,
              evaluationSubFactors: ri!evaluationSubFactors,
              index: fv!index,
              bundle: ri!bundle,
              isSubFactor: true,
              evaluatorTeams: ri!evaluatorTeams
            ),
            /*Evaluators*/
            rule!AS_GSS_PCK_evaluatorsInUpdateEvaluators(
              currentEvaluationFactor: fv!item,
              evaluationSubFactors: ri!evaluationSubFactors,
              index: fv!index,
              bundle: ri!bundle,
              isSubFactor: true,
              userDetails: ri!userDetails,
              evaluatorTeams : ri!evaluatorTeams
            ),
            /*Due date*/
            rule!AS_GSS_PCK_dateFieldInUpdateEvaluators(
              currentEvaluationFactor: fv!item,
              evaluationSubFactors: ri!evaluationSubFactors,
              index: fv!index,
              bundle: ri!bundle,
              isSubFactor: true,
              parentFactorDue: ri!parentFactorDue
            )
          },
          showWhen: fv!item.parentCriteriaId = ri!parentCriteriaId
        )
      ),
      rowHeader: 1,
      accessibilityText: ri!parentFactorName,

    )
  },
  showWhen: and(ri!hasSubFactor, ri!IsExpand),
  padding: "STANDARD",
  marginAbove: "LESS",
  marginBelow: "EVEN_LESS",
  showBorder: false
)

